* {
  animation-name: misalign;
  animation-fill-mode: both;
  animation-duration: 60s;
  animation-delay: 1s;
  animation-timing-function: linear;
}

*:nth-child(4n+1) {
  --rot: 1deg;
}

*:nth-child(4n+2) {
  --rot: -1deg;
}

*:nth-child(4n+3) {
  --rot: -2.4deg;
}

*:nth-child(4n+4) {
  --rot: 1.5deg;
}

p {
  --rot: -2deg;
}

#header {
  --rot: 3deg;
}

a {
  --rot: -4deg;
}

@keyframes misalign {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(var(--rot));
  }
}
