div.authorlink-wrapper{margin-top: var(--author-top-adjust, .2em) !important;}
:root {
--header-logo: url('http://scp-sandbox-3.wikidot.com/local--files/duality/Cycles');
  --header-title: 'Always Remembered';
--sidelogo: url("http://scp-sandbox-3.wikidot.com/local--files/duality/Cycles');
[[/module]]

[[module CSS]]
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon}
[[/module]]
[[module css]]
/*----------------------------*/
/*---- ADMONITION CLASSES ----*/
/*----------------------------*/

.cond {
    font-family: 'Roboto Condensed', var(--UI-font-fallback);
}

.ctrl {
    font-family: var(--header-font);
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: -2px;
    word-spacing: 2px;
}

.widetable :is(table:not(.form), table.wiki-content-table) {
    width: 100% !important;
    margin-top: 1.2rem;
    margin-bottom: 1rem;
}

.acstable :is(table:not(.form), table.wiki-content-table) {
    font-size: 0.9em;
}

.admo-episode_splash, .admo-intermission_splash {
    display: flex;
    flex-flow: column nowrap;
    justify-content: center;
    height: calc(100vh - var(--header-final-height));
    text-align: center;
}

.admo-episode_splash .admo-rate_splash {
    margin-top: -13.5rem;
    padding-bottom: 3.5rem;
}

.admo-intermission_splash .admo-rate_splash {
    margin-top: -21.5rem;
}

:is(.admo-episode_splash, .admo-intermission_splash) .ctrl {
    letter-spacing: -0.25vw;
}

.admo-episode_splash .ctrl {
    font-size: 5vw;
    line-height: 4vw;
}

.admo-intermission_splash .ctrl {
    font-family: var(--mono-font);
    font-size: 5vw;
    line-height: 3vw;
    letter-spacing: 0.5vw;
}

:is(.admo-episode_splash, .admo-intermission_splash) .cond:not(.admo-accessibility .cond) {
    font-size: 2vw;
    opacity: 80%;
}

.admo-episode_splash .cond:not(.admo-accessibility .cond) {
    font-weight: bold;
}

.admo-intermission_splash .cond:not(.admo-accessibility .cond) {
    font-style: italic;
}

@media only screen and (max-width: 1024px) {

    .admo-episode_splash .ctrl {
      font-size: 9vw;
     line-height: 7vw;
    }

    .admo-intermission_splash .ctrl {
     font-size: 8vw;
     line-height: 5.5vw;
    }

    :is(.admo-episode_splash, .admo-intermission_splash) .cond:not(.admo-accessibility .cond) {
     font-size: 3vw;
    }

}

.admo-accessibility {
    font-size: 1.2rem;
    font-weight: bold;
    text-align: center;
}

.admo-accessibility .cond {
    color: #808080;
}

.admo-end_card {
    background: rgba(var(--basalt-secondary-color), 0.5);
    padding: 2rem;
    text-align: center;
}

.admo-end_card .admo-credits {
    display: flex;
    flex-flow: row wrap;
    justify-content: center;
    align-items: center;
    gap: 2rem;
    text-align: left;
}

#u-blink-bar p:last-child::after {
    content: "";
    border-right: 1px solid rgb(var(--basalt-main-text-color));
    animation: blink 1s infinite;
    position: relative;
    left: 1px;
}

@keyframes blink {
    to {
     opacity: 0;
    }
}

.tabanchorbutton li {
    z-index: 1 !important;
    font-weight: bold;
}

.tabanchorbutton a {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}

.fnnumblock ol li::marker {
    color: rgb(var(--admo-color-bright));
    font-family: var(--ui-font);
    font-weight: bold;
}

[[/module]]
