[[include :scp-wiki:theme:foxtrot canada=a|hidetitle=a]]
[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Comic+Neue&display=swap');
:root{
 --header-title: "";
 --header-subtitle: "";
 --logo-img: url(https://scp-wiki.wdfiles.com/local--files/art%3Asweet-phil-and-hella-doug/site%2043%20logo%20real_png.png);
 --logo-opacity: 100%;
 --link-txt-color: #0f0;
 --link-hover-txt-color: white;
 --acc-default: 50, 255, 255;

  --head-font: 'Comic Neue', sans-serif;
  --ui-font: 'Comic Neue', sans-serif;
  --page-font: 'Comic Neue', sans-serif;
  --page-font-size: 1.0rem;
}
body {
  background-image: linear-gradient(to bottom, #ff0000, #f301ec 500px);
  @media (max-width:705px) {
    #header h1 a::before {
      font-size: 0.8em;
      margin-top: 5px;
    }
  }
}

#header #top-bar a {
    color: black;
    background: rgb(var(--accent));
    font-weight: bold;
}

#main-content .page-tags a {
    color: black;
}

.page-options-bottom a {
    color: black;
}

#footer a {
    color: #000;
}

div#extra-div-1 {
    background-size: 300px;
}

@media (max-width: 700px) {

    div#extra-div-1 {
     background-size: 270px;
     top: -3px;
    }

}

@media (max-width: 620px) {

    div#extra-div-1 {
     top: -10px;
    }

}

@media (max-width: 520px) {

    div#extra-div-1 {
     background-size: 245px;
     top: 3px;
    }

.yui-navset .yui-nav a,
/* ---- Link Modifier ---- */
.yui-navset .yui-navset-top .yui-nav a {
    color: #70ffff;
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
    background-color: #70ffff;
    border: unset;
    box-shadow: none;
    box-shadow: none;
}

}
.yui-navset .yui-nav .selected,
/* ---- Selection Modifier ---- */
.yui-navset .yui-navset-top .yui-nav .selected {
    flex-grow: 2;
    margin: 0;
    padding: 0;
    /* ---- Tab Background Colour | [SELECTED] ---- */
    background-image: url(http://scpdsandbox.wdfiles.com/local--files/sweet-phil-and-hella-doug/site%2043%20logo%20real.png);
}

