<img src="http://scp-jp.wdfiles.com/local--files/artwork-enigzamination/1-4">
</div>

<div id="false" style="display:none;">
受験番号が違います。
</div>


<!--以下、パスワードの判定及び表示の分岐。-->
<script>
  function deleteForm(){
    document.openText.style.display="none";
  }

  function showText(){
    var password=document.openText.openPassword.value;

    if (password=="0001"){
      document.getElementById("true").style.display="block";
      deleteForm();
    }else if(password==""){
      alert("受験番号を入力してください");
    }else{
      document.getElementById("false").style.display="block";
      deleteForm();
    }
  }
</script>


<!--内容ここまで-->

<div id="scpHTMLblockResizeSwitch"></div>
[[/HTML]]
