[[include component:toggle-sidebar]]
[[module CSS]]
#header {
    background: url(https://scp-ru.wdfiles.com/local--files/artwork%3Arussian-art-hub/scp-art-hub.png) 10px 40px no-repeat;
    background-size: 100px;
}

.ez-gallery {
    display: flex;
    flex-wrap: wrap;
    padding: 0 4px;
    margin: 1em;
    justify-content: space-evenly;
}

.ez-gallery > div {
    margin-top: auto;
    margin-bottom: auto;
}

#main-content {
    max-width: 90% !important;
}

.scp-image-block {
    margin-left: auto !important;
    margin-right: auto !important;
}

#header h1 a::before {
    content: 'SCP-RU艺作中心';
    color: white;
    text-shadow: 1px 1px 0px black;
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

#header h2 {
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: 'Pastarasta1的创意页面';
    color: #f0f0c0;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
[[/module]]

