[[module CSS]]
:root {
    --bright-accent: 1, 82, 174;
    --medium-accent: 0, 56, 123;
    --dark-accent: 0, 29, 64;
    --barColour: #0152ae!important;
}
:root:lang(cn) {
    --header-title: "蓝图";
    --header-subtitle: "等下，什么？";
}

#content-wrap::before {
    background-image: url('http://world-database.wikidot.com/local--files/page-files/hexagontrans.png');
    background-size: 70%;
}
#extra-div-2 {
    background-image: url('http://world-database.wikidot.com/local--files/page-files.png');
    background-size: 60%;
    opacity: 50%;
}
#extra-div-3 {
    filter: hue-rotate(220deg);
}

.wp{
    box-shadow: 0px 0px 7px rgba(0,0,0,0.4);
    transition: 0.34s ease-in-out transform, 0.38s ease-in-out box-shadow;
}
.wp:hover {
    box-shadow: -2px 0px 8px rgba(0,0,0,0.4);
}
.odd:hover {
    transform: perspective(95em) rotateY(10deg);
}
.even:hover {
    transform: perspective(95em) rotateY(-10deg);
}

.links {
    display: block;
    text-align: center;
    border: 4px solid rgb(var(--bright-accent));
    margin: auto;
    padding: 0 10px;
    border-radius: 10px;
    margin-bottom: 15px;
}
.links a{
    background: rgb(var(--bright-accent));
    color: white;
    padding: 3px;
    border-radius: 4px;
    font-family: monospace;
    white-space: nowrap;
}
.links a:visited {
    color: white;
}
.links a:hover {
    color:rgb(var(--white-monochrome));
}
[[/module]]
