[[module CSS]]
:root {
    --bright-accent: 1, 82, 174;
    --medium-accent: 0, 56, 123;
    --dark-accent: 0, 29, 64;
    --header-title: "BLUEPRINT";
    --header-subtitle: "Wait, what?";
    --barColour: #0152ae!important;
}

#content-wrap::before {
    background-image: url('https://scp-wiki.wdfiles.com/local--files/artyurt/hexagontrans.png');
    background-size: 70%;
}
#extra-div-2 {
    background-image: url('https://scp-wiki.wdfiles.com/local--files/artyurt/hexagonstrans.png');
    background-size: 60%;
    opacity: 50%;
}
#extra-div-3 {
    filter: hue-rotate(220deg);
}

.wp{
    box-shadow: 0px 0px 7px rgba(0,0,0,0.4);
    transition: 0.34s ease-in-out transform, 0.38s ease-in-out box-shadow;
}
.wp:hover {
    box-shadow: -2px 0px 8px rgba(0,0,0,0.4);
}
.odd:hover {
    transform: perspective(95em) rotateY(10deg);
}
.even:hover {
    transform: perspective(95em) rotateY(-10deg);
}

.links {
    display: block;
    text-align: center;
    border: 4px solid rgb(var(--bright-accent));
    margin: auto;
    padding: 0 10px;
    border-radius: 10px;
    margin-bottom: 15px;
}
.links a{
    background: rgb(var(--bright-accent));
    color: white;
    padding: 3px;
    border-radius: 4px;
    font-family: monospace;
    white-space: nowrap;
}
.links a:visited {
    color: white;
}
.links a:hover {
    color:rgb(var(--white-monochrome));
}
[[/module]]
