/* Troposphere
    [2023 Wikidot Theme]
    By AI_Mayca
    Created for the SCP Foundation
    Background image licensed under CC0 */
@import url(https://fastly.jsdelivr.net/gh/Etinjat/Font-CSS@main/Typeface-Liu_ti.css);
html, body {
    color: #DDD;
}
div#container-wrap, #header {
    background-image: none;//a57f53 4e6167
}
#header h1 a::before {
    content: "失温症";
}
#header h2::before {
    content: "无温无声 向死而行";
    color: #DDD;
    font-size: 0.8rem;
}
#header h1, #header h2 {
    width: 100%;
    display: block;
    text-align: center;
    margin: 0;
}
div#container-wrap::before {
    content: "";
    display: block;
    background-image: url(https://scpsandboxcn.wdfiles.com/local--files/collab:mayca/对流);
    background-size: cover;
    background-position: center;
    filter: brightness(0.6);
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
}
#page-content {
    color: #DDD;
    font-size: 0.9rem;
    font-family:'Liu ti', sans-serif;
}
#header h1 span, #header h2 span {
    display: none;
}
#search-top-box-input, #search-top-box-form input[type="submit"] {
    color: #DDD;
    background-color: rgba(0, 0, 0, 0.3);
    border-color: #DDD;
    border-width: 2px;
    border-radius: 0;
}
#search-top-box-form input[type="submit"] {
    background: transparent;
}
#page-title {
    color: #DDD;
    text-align: center;
}
#side-bar, .owindow, .content, .alert, #lock-info {
    background-color: rgba(0, 0, 0, 0.7);
}
.owindow div.modal-header {
    background: rgba(0, 0, 0, 0.1);
}
#side-bar .side-block {
    background-color: transparent;
    border: solid 2px #DDD;
    box-shadow: none;
    border-radius: 0;
}
#side-bar .side-block:nth-of-type(2) {
    background-color: transparent !important;
}
#top-bar .top-bar > ul > li:hover a, #top-bar .mobile-top-bar > ul > li:hover a, #top-bar .top-bar > ul > li:hover > ul > li a, #top-bar .mobile-top-bar > ul > li:hover > ul > li a {
    background-color: rgba(0, 0, 0, 0.7);
    border-color: transparent;
}
#top-bar .mobile-top-bar .open-menu p a {
    color: #DDD;
    background-color: rgba(0, 0, 0, 0.5);
    border-color: #DDD;
    border-radius: 0;
}
.page-rate-widget-box {
    color: #DDD;
    border-radius: 0;
    box-shadow: none;
}
.page-rate-widget-box .rate-points, .page-rate-widget-box .rateup, .page-rate-widget-box .ratedown, .page-rate-widget-box .cancel {
    background-color: rgba(0, 0, 0, 0.5);
    border-color: #DDD;
    border-width: 2px;
    border-radius: 0;
    padding: 2px;
}
.page-rate-widget-box .rate-points {
    background: rgba(0, 0, 0, 0.5) !important;
    padding-left: 5px;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a, .page-rate-widget-box .cancel a {
    color: #DDD;
}
.page-rate-widget-box span.cancel a[title="取消我的投票"] {
    border-radius: 0;
}
.page-rate-widget-box span.rateup:hover, .page-rate-widget-box span.ratedown:hover, .page-rate-widget-box span.cancel:hover {
    background-color: #DDD;
}
.page-rate-widget-box span.rateup:hover a, .page-rate-widget-box span.ratedown:hover a, .page-rate-widget-box span.cancel:hover a {
    background-color: #DDD;
    color: rgba(0, 0, 0, 0.7);
}
.rateBox .rate-box-with-credit-button {
    background-color: transparent;
    border: none;
    border-radius: 0;
    box-shadow: none;
    padding: 0;
}
.rateBox .rate-box-with-credit-button .creditButton {
    border: solid 2px #DDD;
    background-color: rgba(0, 0, 0, 0.5);
    padding: 3px 1px;
    padding-left: 5px;
}
.rateBox .rate-box-with-credit-button .creditButton p a {
    border: none;
}
.rateBox .rate-box-with-credit-button .page-rate-widget-box span.cancel {
    border-right: solid 2px #DDD;
}
#u-credit-view:target .modalbox {
    background: rgba(0, 0, 0, 0.7);
}
[[/mOdUlE]]
