[[module CSS]]

/* 版式标题和logo */

div#header h1 a span {
    font-size: 0px;
}

div#header h1 a:before, 
div#header h2:after {
    font-weight: bold;
    color: white;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

div#header h1 a:before {
    font-size: 90%;
    content: "基金会权限卡管理系统";
    text-shadow: 1.5px 1.5px 1.5px rgba(0, 0, 0, .8);
}

div#header h2 span {
    font-size: 0px;
    padding: 4px;
}

div#header h2:after {
    font-size: 90%;
    content: "AuthCard Management System";
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
}

div#header {
    background-image: url(https://scpsandboxcn.wikidot.com/local--files/collab:card/logo-large-1000px.png);
}

@media (min-width: 979px) {
div#header {
    background-size: 100px 100px;
}
}

/* 翻转卡片 */

.card {
    --card-width: calc(80px + 40vw);
    --card-height: calc(var(--card-width) * 0.64166667);
    --card-radius: calc(var(--card-width) * 0.05);
    --card-perspective: calc(var(--card-width) * 1.25);

    position: relative;
    -webkit-perspective: var(--card-perspective);
    -moz-perspective: var(--card-perspective);
    perspective: var(--card-perspective);
    margin: 2em auto;
    width: var(--card-width);
    height: var(--card-height);
}
.card .front, .card .back {
    position: absolute;
    top: 0;
    left: 0;
    backface-visibility: hidden;
    background-size: contain;
    background-repeat: no-repeat;
    -webkit-transition: -webkit-transform 1s ease-out;
    -moz-transition: -moz-transform 1s ease-out;
    -o-transition: -o-transform 1s ease-out;
    -ms-transition: -ms-transform 1s ease-out;
    transition: transform 1s ease-out;
    border-radius: var(--card-radius);
    width: var(--card-width);
    height: var(--card-height);
}
.card .back {
    -webkit-transform: rotateY(180deg);
    -moz-transform: rotateY(180deg);
    -o-transform: rotateY(180deg);
    -ms-transform: rotateY(180deg);
    transform: rotateY(180deg);
    text-align: center;
}
.card:hover .front {
    -webkit-transform: rotateY(180deg);
    -moz-transform: rotateY(180deg);
    -o-transform: rotateY(180deg);
    -ms-transform: rotateY(180deg);
    transform: rotateY(180deg);
}
.card:hover .back {
    -webkit-transform: rotateY(360deg);
    -moz-transform: rotateY(360deg);
    -o-transform: rotateY(360deg);
    -ms-transform: rotateY(360deg);
    transform: rotateY(360deg);
}

/* 邮件闪烁折叠 */

.email-example .collapsible-block {
    text-align: center;
}

.email-example a.collapsible-block-link {
    font-weight: bold;
}

.email-example .collapsible-block-folded a.collapsible-block-link,
.nested-email-example .colmod-link-top {
    animation: blink 0.8s ease-in-out infinite alternate;
}

@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: rgb(0, 0, 170); }
}

/* 折叠样式 */

.email-example .colmod-block {
    margin-top: 2em;
}

.email-example .colmod-link-top a {
    color: rgb(0, 0, 170);
    font-size: 200%;
    font-weight: bold;
}

.email-example .colmod-link-top, 
.mn-container,
blockquote,
.blockquote {
    text-align: left;
}

.nested-email-example .colmod-link-top, 
.tip-container {
    text-align: center;
}

.nested-email-example .colmod-link-top a {
    font-size: 100%;
}

/* 部分图片预加载 */
.preload {overflow: hidden;}

.preload img {
    opacity: 0;
    height: 0px;
}
