[[module CSS]]
@charset "UTF-8";
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@200..900&display=swap');

#content-wrap {
    min-height: unset;
    max-width: 60em;
}

#html-body #container-wrap {
    margin-top: 0;
    background: none;
}

#html-body #header {
    background: none;
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    height: unset;
    width: 100%;
}

#page-title,
#html-body #header h1,
#html-body #header h2,
#html-body #header #search-top-box,
#html-body #header #top-bar,
#html-body #content-wrap #side-bar {
    display: none;
}

#main-content {
    margin: 2em 0.5em 0px;

}

#html-body #header #login-status,
#html-body #header #login-status>a {
    color: gray;
}

#html-body #extra-div-1 {
    width: 100vw;
    height: 100vh;
    position: fixed;
    top: 0;
    left: 0;
    z-index: -2;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}

#page-content,
#page-content h1,
#page-content h2,
#page-content h3,
#page-content h4 {
    font-family: "Noto Serif JP", serif;
    color: black;
}

#page-content {
    min-height: 100lvh;
    font-weight: 500;
    font-size: 18px;
}

#page-content h1 {
    font-weight: 700;
    font-size: 26px;
    text-align: center;
    margin-top: 50px;
}

#page-content h2,
#page-content h3,
#page-content h4 {
    font-weight: 400;
    font-size: 24px;
    text-align: center;
    margin-top: 40px;
}

.link-box a {
    color: rgb(76, 102, 128);
}

.link-box a:hover {
    text-decoration: none;
}

#login-status {
    position: absolute;
    top: 20px;
}
[[/module]]

