[!-- ------------------------------ここからCSS----------------------------------- --]
[[module css]]
/* 背景 */
body {
    background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6169929-42-q8ln/sea-night-ocean-dark3-2.png) bottom repeat-x;
}

/* ヘッダー */
div#container-wrap {
    background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6169929-41-j3mm/sea-night-ocean-dark2-2.png) top left repeat-x;
}
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "BED time";
    color: #fff;
    text-shadow: 3px 3px 5px #000;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "Blue, Evening, Dream";
    font-weight: bold;
    color: #fff;
    padding: 19px 0;
    text-shadow: 1px 1px 1px #000;
    white-space: pre;
}

/* 検索ボタン */
#search-top-box-form input[type=submit] {
    border: solid 1px #999;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #ddd;
    background-color: #4c4c6c; 
    background: linear-gradient(to bottom, #f0f0f8,#787c9c,#4c4c6c);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
/* 検索ボタンホバー時 */
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: ##4c4c6c;
    background: linear-gradient(to bottom, #f0f0f8,#787c9c,#4c4c6c);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}
/* 検索箱 */
#search-top-box-input {
    border: solid 1px #999;
    color: #ccc;
    background-color: #2c2e3c; 
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.5);
}
/* 検索箱ホバー時 */
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #fff;
    color: #fff;
    background-color: #787c9c; 
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}


/* 見出し */
h1, #page-title {
    color: #2c2e3c;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
/* 見出し２以降 */
h2, h3, h4, h5, h6, h7 {
    color: #2c2e3c;
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
}

/* リンク */
a {
    color: #069; 
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #0a7; 
    text-decoration: none;
    background: transparent;
}
a:hover {
    text-decoration: underline;
    background-color: transparent;
}

/* タブ */
.flieNav .yui-navset .yui-nav > li > a em:before{
 content:"";
    /*font-awesome fa*/
    font:normal normal normal 14px/1 FontAwesome;
    transform:translate(0, 0);
    display:inline-block;
    -webkit-font-smoothing:antialiased;
    -moz-osx-font-smoothing:grayscale;

}
.flieNav .yui-navset .yui-nav > li > a em:before{
    /*folder*/
    content:"\f07b";
}
.flieNav .yui-navset .yui-nav > .selected > a em:before{
    /*folder-open*/
    content:"\f07c";
}

div.yui-navset div.yui-content {
    border: solid #265 1px;
    background: linear-gradient(270deg, #dfe9f3, #f6f5f5);/* 背景グラデ */
    padding-left: 1em;
}

div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    border: solid #265 1px;
    background: #ffffff;
    color: #265;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #9bbfac;
    color: #fff;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    border: solid #265 1px;
    background: #265;
    color: #fff;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #265;
}
.yui-navset li {
    line-height: 145%;/* 行高さ */
    word-break: break-all;/* コンテンツBox内改行指定 */
}
.yui-nav li a em{
border-top:solid #265 1px !important;
}


/* 左線ありブロック */
.box1{
    padding: 0.5em 1em;
    margin: 2em 0;
    color: #2c2e3c;
    background: linear-gradient(130deg, #f6f5f5, #ffffff);
    border-left: solid 5px #265;
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.22);
}
.box1 p {
    margin: 0; 
    padding: 0;
}

/* 点線ブロック背景グラデ */
.box2 {
    padding: 0.5em 1em;
    margin: 1em 0;
    border: dashed 1px #265;/*点線*/
    background: linear-gradient(130deg, #fef9d7, #eed8e8);
    box-shadow: 0 3px 5px rgba(0, 0, 0, 0.22);
}
.box2 p {
    margin: 0; 
    padding: 0;
}

/* グラデーションボックス */
.box3{
    padding: 0.5em 1em;
    background: -moz-linear-gradient(#023, #fff);
    background: -webkit-linear-gradient(#023, #fff);
    background: linear-gradient(to right, #023, #fff);
    color: #FFF;
}
.box3 p {
    margin: 0; 
    padding: 0;
}

/* 明朝体 */
.font1 {  
    font-family: 'Yu Mincho Light','YuMincho','Yu Mincho','游明朝体','ヒラギノ明朝 ProN','Hiragino Mincho ProN',serif;
    line-height: 145%;
}

[[/module]]
