[[module CSS]]
/* machineTab customization */
.machineTab .yui-navset .yui-nav li:nth-child(2),
.machineTab .yui-navset .yui-nav li:nth-child(-n+2).selected {
    display: none;
}
.machineTab .yui-navset .yui-nav li:nth-child(1).selected ~ li:nth-child(2) {
    display: inline-block;
}
.machineTab .yui-navset .yui-nav li:nth-child(-n+2) {
    width: 100%;
}
.machineTab .yui-navset .yui-nav li:nth-child(-n+2).selected {
    transform: none;
}
.machineTab .yui-navset .yui-nav li:nth-child(1)::after {
    background-color: #d2691e;
}
.machineTab .yui-content #wiki-tab-0-0 {
    background: #000;
}
.machineTab .yui-content #wiki-tab-0-0::before,
.machineTab .yui-content #wiki-tab-0-0::after {
    display: none;
}

/* workbench */
.workbench .yui-navset .yui-nav li:last-child {
    display: none;
}
.iti119 + .workbench .yui-navset .yui-nav li:last-child {
    display: inline-block;
    width: 100%;
}
[[/module]]
