content:
[[include :scp-wiki:theme:monotypical]]
[[module CSS]]

#header::before {
    opacity: 1;
}
#page-title{
    display: none;
}
@media screen and (min-width: 481px) {
  --header-background-image-size: 60%;
}

:root {
    --themeColor: #2aa198;
    --accentColor: #eeeeee;
    counter-reset: counter 0;
    --linkColor: #bbe4ff;

    --primaryBackgroundColor: #eeeeee;
    --secondaryBackgroundColor: #eeeeee;
    --tertiaryBackgroundColor: #e5e5e5;
    --borderColor: #93a1a1;

    --shadow: rgba(0,0,0,0.2);

    --selectionColor: #bbe4ff;
    --textColor: #268bd2;
    --textColor-alt: #268bd2;
}
#side-bar .heading, #license-area {
    --textColor: 6caad3;
}

}
body {
    background: #eeeeee;
    color: #555555;
    font-family: 'Open Sans';
}
a,a:visited {
    color: #268bd2;
}
h1,h2,h3,h4,h5,h6 {
    color: #555555;
    font-family: 'Open Sans';
}
div#side-bar div.side-block {
    background: #eeeeee;
    border: 1px solid #2aa198;
    box-shadow: 0 2px 6px rgba(#bbe4ff);
    border-radius: 5px;
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: #268bd2;
    border-color: #268bd2;
}
div.yui-navset div.yui-content {
    background: #dddddd;
}
div.yui-navset ul.yui-nav a,
div.yui-navset div.yui-navset-top ul.yui-nav a {
    background: #dddddd;
    color: #555555;
    border: solid #555555;
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: #eeeeee;
    color: #000000;
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #dfdfdf;
    color: #555555;
    border: solid #555555;
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #e5e5e5!important;
    border: solid #e5e5e5 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #e5e5e5!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #555555;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #e5e5e5!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #555555!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}
div.open-menu a {
    color: #555555!important;
    border-color: #555555!important;
}

@keyframes c-change {
    from {color: #eeeeee; }
    to {color: #555555;}
}

[[/module]]

