[[html]]
<!-- スロットのコンテナ -->
    <div id="slot_container">
        <!-- スピンボタン -->
        <button onclick="spin()" class="spin-button">クリック！</button>
        <!-- チェックボックス -->
        <div id="canon_selection">
            <label for="includeEn">カノン-EN</label>
            <input type="checkbox" id="includeEn" checked>
            <br>
            <label for="includeJp">カノン-JP</label>
            <input type="checkbox" id="includeJp" checked>
        </div>
        <!-- 結果表示エリア -->
        <div id="slot_result"></div>
    </div>

    <style>
        body {
            font-family: 'sans-serif';
            text-align: center;
            background-color: #f0f0f0;
            margin: 0;
            padding: 0;
            height: 100vh;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        /* スロットのコンテナのスタイル設定 */
        #slot_container {
            display: flex;
            align-items: center;
            justify-content: center;
            flex-direction: column;
        }

        /* スピンボタンのスタイル設定 */
        .spin-button {
            padding: 10px 20px;
            font-size: 16px;
            background-color: #2647a0;
            color: #fff;
            border: none;
            cursor: pointer;
            border-radius: 5px;
            transition: background-color 0.3s ease;
            display: inline-block;
        }

        /* スピンボタンのホバーエフェクトのスタイル設定 */
        .spin-button:hover {
            background-color: #0530a3;
        }

        /* チェックボックスのスタイル設定 */
        #canon_selection {
            margin-top: 20px;
            padding-left: 10px;
            align-items: center;
        }

        /* 結果表示エリアのスタイル設定 */
        #slot_result {
            font-size: 24px;
            margin-top: 20px;
            padding: 20px;
            height: 150px;
            border: 2px solid #2647a0;
            border-radius: 5px;
            background-color: #fff;
            display: flex;
            align-items: center;
            justify-content: center;
            word-wrap: break-word;
        }

        @media screen and (min-width: 600px) {
            #slot_result {
                width: 400px;
            }

        }

        @media screen and (max-width: 500px) {
            #slot_result
            {
                width: 250px;
            }
        }
    </style>

    <!-- JavaScriptコード -->
    <script>
        // 単語のリスト
        const canonEn = ['死人の手札', 'アド・アストラ・ペル・アスペラ', 'AIAD', '南極の交流', 'アポセオシス', '鐘を鳴らす者の詩', '壊された虚構',
            'コードネーム:翠の王', '極寒の戦争', '競り合う終末論', 'クールな戦争2', '破暁', '教会の博士', 'ドレッド&サーカス', '死の終焉',
            'そして私は神を求めた', '120の記録書庫より', '財団錬金術部門', 'メキシコ湾', '追憶のアディトゥム', '昆虫地獄', 'たのしいざいだん', '存在しなかった者',
            'ナイトフォール', 'ノー・リターン', '老人との海', 'オンガード43', 'ゴルゴタの丘で', '第九世界', '最上の事', 'ㄗitch 卄aven', 'ヘイムダル計画',
            'ラッツネスト', '甦──リザレクション', 'S&Cプラスチック', 'オルカディアの海', 'ボトルシップ', 'サイト-17深淵目録', '団結号を奪え', '朝までまっすぐに',
            '第三法則', 'あのヨレハマツの林', 'トラッシュファイア', '財団のない世界', '人ならぬもの', '全ての戦線で戦え', 'この素晴らしき世界'];
        const canonJp = ['わるいざいだん', 'ODSS', '扶桑紀', '1998年', 'スシブレード', 'ファウンデーション・コレクティブ', 'アブサンの夢', '四辻喜劇', '筐体造り',
            '聖杯を仰ぐ翳', '共異廻歴', '相貌失認', 'ちいさなざいだん', '極夜灯', '二重の故郷', 'Thanatomania', '大正150年', 'まずしいざいだん', '氷我記', '怪奇銘々伝'];
        // スピンボタンがクリックされたときの処理
        function spin() {
            // チェックボックスの状態に基づいて単語のリストを構築
            const includeEn = document.getElementById('includeEn').checked;
            const includeJp = document.getElementById('includeJp').checked;
            if (!includeEn && !includeJp) {
                alert("少なくとも1つの支部を選択してください");
                return;
            }

            let wordList = [];

            if (includeEn) {
                wordList = wordList.concat(canonEn);
            }

            if (includeJp) {
                wordList = wordList.concat(canonJp);
            }

            // ランダムに2つの単語を選択
            let word1, word2;
            do {
                word1 = getRandomWord(wordList);
                word2 = getRandomWord(wordList);
            } while (word1 === word2);

            // 結果を表示
            const resultElement = document.getElementById('slot_result');
            resultElement.innerHTML = `${word1}<br>✕<br>${word2}`;
        }

        // ランダムな単語を取得する関数
        function getRandomWord(wordList) {
            const randomIndex = Math.floor(Math.random() * wordList.length);
            return wordList[randomIndex];
        }
    </script>

