/* 掲示板 */
div.CorkBoard {
	border: ridge 8px Olive;
	background-color: White;
	background-image: url(http://ja.scp-wiki.net/local--files/author:sanks269/cork.jpg);
}
div.paper {
	float: center;
	width: 75%;
	border: solid 1px #111111;
	box-shadow: 0 1px 6px rgba(0, 0, 0, 0.25);
	background: #fefefe;
	padding: 5px 30px;
	margin: 0 auto;
}
.circle {
	width: 16px;
	height: 16px;
	display: inline-block;
	border-radius: 50%;
	background: red;

	font-size: large;
	text-align: center;
	vertical-align: middle;
}

/* ページスタイル */

/* 背景 */
body {
	background-color: #e0ffff;
}
div#container-wrap {
	background: url(http://ja.scp-wiki.net/local--files/author:sanks269/body_bg_e0ffff.png)
		top left repeat-x;
}

/* ページタイトル */
#page-title {
	display: none;
}



.fake_title {
	font-size: 200%;
	color: #990011;
	padding: 0 0 0.25em;
	margin: 0 0 0.6em;
	border-bottom: 1px solid #bbb;
	font-weight: normal;
}
.fake_title p {
	padding: 0;
	margin: 0.25em 0 0;
}



.fake_title_tb {
	text-align: center;
	font-size: 200%;
	color: #990011;
	padding: 0 0 0.25em;
	margin: 0 0 0.6em;
	border-top: 1px solid #bbb;
	border-bottom: 1px solid #bbb;
	font-weight: normal;
}
.fake_title_tb p {
	padding: 0;
	margin: 0.25em 0 0;
}

.forClickChangePageTitleDiv {
	font-size: 200%;
	color: #990011;
	padding: 0 0 0.25em;
	margin: 0 0 0.6em;
	border-bottom: 1px solid #bbb;
	font-weight: normal;
}
.forClickChangePageTitleDiv .collapsible-block-link {
	color: #990011;
	padding: 0;
	margin: 0;
	font-weight: normal;
	text-decoration: none;
}
.forClickChangePageTitleDiv .collapsible-block-link:hover {
	color: #990011;
	padding: 0;
	margin: 0;
	font-weight: normal;
	text-decoration: none;
}
.forClickChangePageTitleDiv p {
	padding: 0;
	margin: 0;
}

/* 汎用クラス */

/* 虹文字 */
.grad {
	background: -webkit-linear-gradient(
		0deg,
		#8b00ff,
		#ff0000,
		#ff7f00,
		#ffff00,
		#00ff00,
		#00ffff,
		#0000ff
	);
	background-clip: text;
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
}

/* 点滅文字 */
.blink {
	word-break: break-all;
	font-weight: bold;
	opacity: 0;
	animation: blink 1.8s infinite;
}
@-webkit-keyframes blink {
	0% {
		opacity: 0;
	}
	1% {
		opacity: 1;
	}
	70% {
		opacity: 1;
	}
	71% {
		opacity: 0;
	}
	100% {
		opacity: 0;
	}
}
@-moz-keyframes blink {
	0% {
		opacity: 0;
	}
	1% {
		opacity: 1;
	}
	70% {
		opacity: 1;
	}
	71% {
		opacity: 0;
	}
	100% {
		opacity: 0;
	}
}
@keyframes blink {
	0% {
		opacity: 0;
	}
	1% {
		opacity: 1;
	}
	70% {
		opacity: 1;
	}
	71% {
		opacity: 0;
	}
	100% {
		opacity: 0;
	}
}
