[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=DotGothic16&display=swap');
@media (min-width: 768px){
    #side-bar{
        background-color: white
    }
}

div#header{
    background-image: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A4957671-6-xdfg/snma_dot_icon.png);
    background-size: 100px 100px;
}

#html-body,h1,h2,h3,h4,h5,h6,#login-status,#header h1 span::before,#header h2 span::before {
    font-family:  "DotGothic16", sans-serif;
}

#header h1 span::before{
   content:"Snma Department";
}

#header h2 span::before{
   content:"Sなんちゃら Nなんちゃら Mなんちゃら Aなんちゃら";
}

#header h1 span::before, #header h2 span::before{
    white-space: nowrap;
}

@media (max-width: 767px) {
    div#header {
        max-width: 90%;
        background-size: calc(48px + 5%);
        background-position: 8px 64%;
    }

   #header h2 span::before{
        content:"SNMAは略語ではない" !important;
    }
}


.code{
    border: 1px solid #ddd;
    border-radius: 10px;
    padding: 3px 10px;
    font-family: "DotGothic16",'Andale Mono', 'Courier New', Courier, monospace;
    line-break: anywhere;
}

.code span.type{
    color: #800080;
}

.code span.func{
    color: #000084;
}

.code span.str{
    color: #008484;
}

.code span.opt{
    color:#dd6611;
}

.code span.ans, .code div.ans{
    color:#666;
    font-style: italic;
}

.list-pages-box ol{
    padding-inline-start: 1.5em;
}

.list-pages-box:not(:has(ol)){
    display:none;
}

span.shutdown:after{
    content: "...";
    animation: progress 2s infinite;
}

@keyframes progress {
  0% {
    content: "";
  }

  20% {
    content: "";
  }

  40%{
    content: ".";
  }

  60% {
    content: "..";
  }

  80% {
    content: "...";
  }

  100% {
    content: "...";
  }
}

.yui-nav{
    display: flex;
    flex-wrap: wrap
}

.yui-nav li{
    flex-grow: 1;
}

.yui-nav li a{
    width: 100%
}
[[/module]]
