[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Work+Sans:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@100;200;300;400;500;600;700;800;900&display=swap');

:root {
    --accentColor: #a869be;
    --lgurl: url(http://scp-jp.wdfiles.com/local--files/author:ukarayakara/vases.png);
    --header-title: "花瓶の埃";
    --header-subtitle: "不在 - 虚無 - 架空";
}
div#side-bar{
    background: #FFFAF0;
    clear: both;
    padding: .8em;
    border-radius: 0;
    box-shadow: none;
    overflow-x: hidden;
}

#side-bar {
     left: -23em;
    }

#top-bar div.open-menu a {
    border-radius: 0;
    box-shadow: none;
    color: var(--accentColor);
    background-color: #FFFAF0;
    border: 1px groove var(--accentColor);
}

body {
    color: #030F2C;
    background-color: #FFFAF0;
    background-image: linear-gradient(
        to bottom,
        #FFFAF0, #FFFAF0 90px,
        #FFFAF0 90px, #FFFAF0 200px,
        #FFFAF0 200px, #FFFAF0 100%);
    background-repeat: no-repeat;
}


#login-status {
    color: #a869be;
}
#login-status a {
    color: var(--accentColor);
}
#login-status ul a {
    color: #030F2C;
    background: #21252E;
}
#login-status ul a:hover {
    color: var(--accentColor);
}

#account-topbutton{
    border: solid 1px var(--accentColor);
   var(--accentColor);
}

#footer, #footer a {
    background: transparent;
    color: #ededed;
}

#header h1 a::before, #header h2::before {
letter-spacing: 2px;
font-family: 'Work Sans', sans-serif !important;
font-weight: 700 !important;
}


.page-source, tt{
    font-family: "Fira Code", monospace;
    font-size: 0.87rem;
}
 .code pre, .code p, .code {
    font-family: "Fira Code", monospace;
    font-size: 0.87rem;
    color: #ededed;
}

#top-bar, #top-bar a {
     color: #030F2C;
     transition: color 0s;
}

h1, h2, h3, h4, h5, h6 {
color: var(--accentColor);
font-family: 'Work Sans', sans-serif !important;
font-weight: 700 !important;
text-transform: uppercase !important;
}

#header h2::before {
font-size: 1em;
}

#header h1 a::before {
font-size: 1em;
letter-spacing: 5px;
}

#login-status {
    color: var(--accentColor);
}
#login-status a {
    color: var(--accentColor);
}
#login-status ul a {
    color: var(--accentColor);
    background: #21252E;
}
#login-status ul a:hover {
    color: var(--accentColor);
}

#account-topbutton{
    border: solid 1px var(--accentColor);
}

#footer, #footer a {
    background: transparent;
    color: #ededed;
}
#license-area {
     color: #000000;
}


.allmont, .allmont *, .allmont *::before, .allmont *:before, .allmont *::after, .allmont *:after {
font-family: 'Work Sans', sans-serif !important;
font-weight: 600;
}

@media (min-width: 768px) {
    #main-content {
       max-width: 98%;
    }
}

.notation {
    border-left: solid 3px rgb(40, 40, 40);
    border-right: solid 3px rgb(40, 40, 40);
    padding: 5px 15px;
    box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, 0.7);
    width: 75%;
    margin: auto;
    background: rgb(20, 20, 20) !important;
    overflow: auto;
    height: auto;
}

.modal {

    padding: 15px 15px 15px 15px;
    box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, 0.7);
    width: 96%;
    margin: auto;
    background: rgb(20, 20, 20);
    border: solid 2px rgb(40, 40, 40);
    overflow: auto;
    height: auto;
}

.bigtext {
    font-family: 'Work Sans', sans-serif;
    font-weight: 700;
    color: var(--accentColor);
    font-size: 110%;
}

blockquote,
div.blockquote,
#toc {
    color: #FFFAF0;
    background-color: #21252E;
    border: #21252E 2px solid;
    border-left: 5px dashed var(--accentColor);
    box-shadow: 4px 4px #2F333C;
}

.scp-image-block .scp-image-caption p {
    font-size: 110%;
    font-family: 'Work Sans', sans-serif;
   color: #ededed;
}

.scp-image-block {
    border: none;
    box-shadow: none;
    color: #ededed;
}

.scp-image-block .scp-image-caption {
    border-top: none !important;
    margin-top: 8px;
    background: rgb(10, 10, 10);
    color: #ededed;
}

.scp-image-block img,
.scp-image-block .scp-image-caption {
    outline: solid 1px var(--accentColor);
    box-shadow: 0px 0px 5px 4px rgba(0, 0, 0, 0.7);
    color: #ededed;
}

div#extra-div-1 {
    filter: opacity(0.4);
}

/* excerpt div stuff */
.excerpt {
  border: solid 2px var(--accentColor);
  border-left-width: 10px;
  padding: 1px 15px;
  width: 75%;
  margin: auto;
  margin-top: 20px;
  margin-bottom: 20px;
  border-radius: 2px;
  background: #303040;
}
@media (max-width: 479px) {
  .excerpt {
    width: auto;
    margin-left: 0;
    margin-right: 0;
  }
}

.hovertip {
    font-size: .81rem;
    background-color: #FFFAF0 !important;
    border: solid 1px var(--accentColor) !important;
}
.footnotes-footer {
    background-color: #FFFAF0;
    padding-left: 1.4rem;
    padding-right: 1.4rem;
    padding-bottom: 1.5rem;
    box-shadow: -0.24rem 0px 0px 0px var(--accentColor);
}
/* Pop-Up Windows */
.owindow {
    background-color: #21252E;
    border-color: var(--accentColor);
    color: #ededed;
}

.owindow .modal-header {
    background-color: #21252E;
    color: #ededed;
}

.owindow .modal-body img {
    background-color: transparent !important;
    color: #ededed;
}

.owindow .title {
    background-color: #21252E;
    color: #var(--accentColor);
    border-bottom: 1px solid #2F333C;
    color: #ededed;
}

.owindow .button-bar a {
    background-color: #21252E;
    border-color: var(--accentColor);
    color: #ededed;
}
.owindow .button-bar a:hover {
    background-color: var(--accentColor);
   color: #ededed;
}

.buttons .btn {
    background-color: #21252E;
    border-color: var(--accentColor);
    color: #ededed;
    padding: 3px 5px;
}
.buttons .btn:hover {
    background-color: #ededed;
    color: #21252E;
}

/* Edit Lock Info*/
#lock-info {
    background-color: #ededed;
    border-color: #ededed;
}

/* Close Button for Page Source, Rating, Etc */
a.action-area-close:hover {
    background-color: #ededed;
    color: #ededed;
}

/* Page-History Current */
.pager .current {
    background-color: #ededed;
    border-color: #ededed;
}

.tableb .wiki-content-table {
    border-collapse: separate;
    border-spacing: 3px;
    background-color: #FFFAF0;
}

.tableb .scp-image-block {
    border: none;
}

.tableb .scp-image-block img {
    border: var(--accentColor) 1px solid;
    box-sizing: border-box;
}

.tableb .scp-image-block .scp-image-caption {
    margin-top: 3px;
    border: var(--accentColor) 1px solid;
    box-sizing: border-box;
}


