                $.tablesorter.addParser({
                    id: 'scpPath',
                    is: function (s) {
                        return false;
                    },
                    format: function (s) {
                        const match = s.match(/scp-(\d+)/i);
                        return match ? parseInt(match[1], 10) : s;
                    },
                    type: 'text'
                });

                $("#scpTable").tablesorter({
                    headers: {
                        0: { sorter: 'scpPath' }
                    }
                });
            });
        }

        // Main function to run everything
        async function main() {
            const entries = await getAllEntries();
            createTable(entries);
        }

        // Run the main function when the page loads
        window.onload = main;
