[[module CSS arguments...]]
#main-content{
  width: 700px;
  background-color: #fafafa;
  font-color: #383838;
  font-size: 14px;
  font-family: -apple-system, BlinkMacSystemFont, “Segoe UI”, Roboto, Helvetica, Arial, sans-serif;
}
@media screen and (min-width: 800px) {
/* 表示領域が800px以上の場合に適用するスタイル */
#main-content{
  background-color: #fafafa;
  font-size: 14px;
  font-family: Helvetica, Arial, “hiragino kaku gothic pro”,meiryo,”ms pgothic”,sans-serif;
  font-weight: 400;
}
}

.canon-main{
  width: 614px;
  margin-left: auto;
  margin-right: auto;
  border: solid 1px #dbdbdb;
  border-radius: 3px;
  background-color: #ffffff;
}

.canon-main strong{
  font-weight: 600;
}

.canon-header{
  height: 60px;
  margin-right: 10px;
  margin-left: 10px;
  margin-top: 0px;
  margin-bottom: 0px;
  padding: 0px;
  display:flex;
  justify-content: space-between;
  align-items: center 
}

.canon-photo{
  margin: 0px;
  padding: 0px;
}

.canon-comment{
  margin-top: 4px;
  margin-left: 16px;
  margin-right: 16px;
  line-height: 18px;
}

.canon-comment p{
  margin-top: 4px;
  margin-bottom: 4px;
}

.canon-comment-goi {
  margin-bottom: 8px;
}

.canon-send{
  border-top: solid 1px #dbdbdb;
  margin-top: -8px;
  padding-left: 16px;
  padding-right: 16px;
  line-height: 18px;
  display:flex;
  justify-content: space-between;
}

[[/module]]

