[[include :ah-sandbox:component:deduction-theme
|title=Site-CN-291
]]
[[module CSS]]
#page-content div.creditRate {
    margin-right: 0;
}

#page-content table.myHeader {
    border: none;
    width: 100%;
}

#page-content div.myImageBlock {
    margin: 2em 0;
    width: 100%;
    border: solid 1px #666;
    background-color: #FAFAFA;
    box-shadow: 0 1px 6px rgba(0,0,0,0.25);
}
#page-content div.myImageBlock img {
    width: 100%;
}
#page-content div.myImageBlock div.myImageCaption {
    text-align: center;
    border-top: solid 1px #666;
}
#page-content div.myImageBlock div.myImageCaption p {
    margin: 4px 1em;
    padding: 0;
}

#page-content div.myBasicBlock {
    border-radius: 8px;
    border: solid 1px #666;
    background: #FAFAFA;
    box-shadow: 0 1px 6px rgba(0,0,0,0.25);
    box-sizing: border-box;
    width: 100%;
    padding: 15px;
    margin: 1em 0;
    display: inline-block;
}

#page-content div.myBlockquote {
    margin: 1.5em 40px;
    padding: 0 1em;
    border: dashed 1px #666;
}
@media (max-width: 767px) {
    #page-content div.myBlockquote {
        margin: 1.5em 0;
    }
}

#page-content span.redacted {
    color: #333;
    background-color: #333;
    user-select: none;
}

#page-content div.myBasicBlock hr {
    margin-left: 0;
}

#page-content tt {
    color: #901;
}

#page-content .memo {
    background-color: #F7F4CB !important;
}

#page-content div.myBasicBlock div.comm div.aaron {
    text-align: right;
    color: #901;
}

#page-content div.myBasicBlock div.comm p {
    padding-right: 38%;
}
#page-content div.myBasicBlock div.comm div.aaron p {
    padding-left: 38%;
    padding-right: 0;
}

#page-content div.comm p::after {
    content: "↲";
}
[[/module]]

