[[module CSS]]
/* INFO BOX */
sup{
    vertical-align: top;
    position: relative;
    top: -0.5em;
}
body{
    --cutColour: #050505;
    --barColour: #e6ac00;
    color: rgb(12, 12, 12);
}
.translation_block, .u-faq, #u-author_block{ display:none; }

/* HEADER */
#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0;
    max-height: 0;
    color: transparent;
    background: transparent;
    font-family: 'Verdana', Arial, sans-serif;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}
#header h1 a::before {
    content: "鳥類部門";
    color: white;
    letter-spacing: -0.02em;
    text-shadow: 3px 3px 5px black;
}
#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0;
    max-height: 0;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}
#header h2 span::before {
    content: '我らの巣を取り戻すまで';
    color: white;
    text-shadow: 1px 1px 1px black;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* RATE MODULE */
div.page-rate-widget-box .rate-points {
    background-color: #c90;
    border: solid 1px #c90;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #fffbf0;
    border-top: solid 1px #c90;
    border-bottom: solid 1px #c90;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #c90;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #c90;
    color: #fffbf0;
}
.page-rate-widget-box .cancel {
    background-color: #c90;
    border: solid 1px #c90;
}
.page-rate-widget-box .cancel a {
    color: #664d00;
}
.page-rate-widget-box .cancel a:hover {
    background: #c90;
    color: #fffbf0;
}

/* FORMATTING */
.bg-gradiant{
    background-color: #fff;
    background: linear-gradient(to bottom, #f7f7f7,#ffc81c,#ffc81c,#fff);
    padding-left: 2%;
    padding-right: 2%;
}
hr{
    display: block;
    border: 0;
    padding: 0;
    margin: 1em 0;
    height: 1px;
}

/* DIV BLOCKS */
.header-image{
    border: 3px solid rgb(12, 12, 12);
    background: #eee;
    padding: 0;
    margin: .5em 0;
}
.showcase{
    border: 3px solid rgb(12, 12, 12);
    background: #fff;
    padding: .5em;
    margin: .5em;
}
.showcase hr{ background: #ffc818; height: 2px; }

/* TABLES */
blockquote,
.article blockquote,
.continued blockquote{
    border: solid 1px #ffc818;
    background: #fff;
    padding: 0;
}
.article table.wiki-content-table,
.continued table.wiki-content-table{
    margin: 0 0 -1px;
    width: 100%;
}
.article table.wiki-content-table th,
.continued table.wiki-content-table th{
    border: solid 1px #ffc818;
    font-weight: normal;
    padding: 0;
}
.article table.wiki-content-table td,
.continued table.wiki-content-table td{
    border: solid 1px #ffc818;
    border-right: 0;
}
.continued table.wiki-content-table td{
    border-top: 0;
}
