[[module css]]
@import url(http://scpsandboxcn.wikidot.com/collab:sleepless-voyage-css/code/1);
[[/module]]
[[html]]

  <div class="wrapper"> <span class="text"><span class="frashing">将烦恼寄存于Ice Bin，</span> <span class="nomal">不妨多来些Rocks？</span></span>
    </span></div>
  <style>
    .wrapper {
      background: black
    }


    .text {


      margin: 5px 0 0 0;
      display: inline-block;
      padding: 2px;
      font-size: 50px;

      font-weight: bold;
      overflow: hidden;
      font-family: '游ゴシック';
      white-space: nowrap;
      animation: flow 15s linear infinite;

    }

    .text::before {
      position: absolute;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      content: ' ';
      display: block;

      background-image: -webkit-linear-gradient(#0a0600 1px, transparent 0px), -webkit-linear-gradient(0, #0a0600 1px, transparent 1px);
      background-size: 3px 3px;
    }

    @keyframes flow {
      0% {
        transform: translate(100%);
      }



      100% {
        transform: translate(-100%);
      }
    }

    .frashing {
      color: #f00
    }

    .nomal {
      color: #FF9F32
    }
  </style>



[[/html]]
