[[module css]]
@import url('https://fonts.googleapis.com/css?family=Eagle+Lake&display=swap');

:root {
--toggle-button-bg: rgb(var(--gray-monochrome));
--toggle-border-color: rgb(var(--dark-gray-monochrome));
--toggle-icon-color: rgb(var(--pale-gray-monochrome));
--toggle-roundness: 0;
}

.icsut {
     border: solid 1px #999999;
     background: rgb(43,30,29);
     margin: 25px 3px;
     padding: .7em;
}

.awcy {
      background-image: linear-gradient(315deg, paleturquoise, #FF9, pink, coral);
      padding: 1px 15px;
      margin-bottom: 25px;
      box-shadow: 2px 2px 2px rgba(0,0,0,.4);
      border: 3px solid transparent;
      border-radius: 10px;
      color: rgb(var(--black-monochrome));
}

.horizon {
     margin: auto;
     padding: 20px;
     border: double 3px rgb(var(--light-pale-gray-monochrome));
     background: rgb(var(--gray-monochrome));
     margin-bottom: 25px;
}

.access {
     margin: auto;
     width: 50%;
     padding: 10px;
     border: dashed 2px rgb(var(--light-pale-gray-monochrome));
}

.page {
    display: block;
    overflow: hidden;
    font-family: 'Eagle Lake', cursive;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top, rgb(187, 191, 158) 0%, rgb(15,16,10)  8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 6em;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
}

.page p {
    line-height: 20px;
    margin: 0;
}

.night {
      border: 2px solid rgb(220,220,200);
      background: rgb(var(--gray-monochrome));
      margin: 1em auto;
      width: 80%;
      padding: 5px;
}

.night a {
     color: rgb(var(--link-color));
     font-weight: bold;
     font-size: 1.5em;
}

.spectre {
    font-family: 'yukarimobileregular';
    font-size: 3.03em;
    letter-spacing: 1px;
    text-align: center;
}
[[/module]]

