[[module CSS]]

/*
    Infinite Riches Theme
    [2025 Wikidot Theme]
    by Fish^12
*/

@import url('https://fonts.googleapis.com/css2?family=STIX+Two+Text:ital,wght@0,400..700;1,400..700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto+Flex:opsz,wght@8..144,100..1000&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Zen+Old+Mincho&display=swap');

/* Root & Sigma+ */

:root {
    --royalblue: #0b1926;
    --navyblue: #193855;
    --yellow: #efd67a;
    --royalpurple: #b083de;
    --grey: #e9e9e9;

    --gold: linear-gradient(60deg, #b78a49, #dbb560, #f5efb5, #dbb560, #b78a49);
    --haze: linear-gradient(60deg, #c600c6, violet, #e995e9, violet, #e000e0);
    --timer: 200ms;
    --diamond: polygon(80% 0, 100% 20%, 50% 100%, 0 20%, 20% 0);

    --stix: "STIX Two Text";
    --robo: "Roboto Flex";
    --zolm: "Zen Old Mincho";

    --header-title: "Black Diamond Billions";
    --header-subtitle: "";
    --sp_header-logo: none;
    --sp_header-title-size: clamp(3rem, -0.714rem + 7.738vw, 6.25rem);
    --sp_header-subtitle-size: 0;

    --sp_header-height: 20rem;
    --sp_top-bar-height: 2rem;

    --sp_top-bar-gradient-top-color: var(--royalblue);
    --sp_top-bar-gradient-bottom-color: var(--royalblue);

    --sp_top-bar-link-color: var(--yellow);
    --sp_top-bar-link-hover-color: var(--royalblue);
    --sp_top-bar-link-hover-background: var(--yellow);

    --sp_dropdown-link-color: var(--royalblue);
    --sp_dropdown-link-hover-color: var(--yellow);
    --sp_dropdown-link-background: var(--yellow);
    --sp_dropdown-link-hover-background: var(--royalblue);
    --sp_dropdown-link-border: none;

    --sp_hovertip-background: var(--royalblue);
    --sp_hovertip-border: 0.2em dotted var(--navyblue);

    --sp_rate-module-background: var(--royalblue);
    --sp_rate-module-text-color: var(--yellow);
    --sp_rate-module-button-color: var(--yellow);
    --sp_rate-module-button-background: var(--royalblue);
    --sp_rate-module-sub-color: var(--yellow);
    --sp_rate-module-button-hover-color: var(--navyblue);
    --sp_rate-module-button-hover-background: var(--yellow);

    --sp_tab-text-color: var(--yellow);
    --sp_tab-background: var(--royalblue);
    --sp_tab-border-color: var(--yellow);
    --sp_tab-hover-text-color: var(--yellow);
    --sp_tab-hover-background: var(--navyblue);
    --sp_tab-hover-border-color: var(--yellow);
    --sp_tab-selected-text-color: var(--royalblue);
    --sp_tab-selected-background: var(--yellow);
    --sp_tab-selected-border-color: var(--yellow);
}

/* From the top! */

#login-status {
    padding-right: 12px;
    font-family: var(--zolm);
    font-size: 16px;
    color: var(--grey);
}
#login-status a:hover {
    text-decoration-style: dotted;
    text-underline-offset: 5px;
}
#login-status a, #login-status ul a {
    color: var(--yellow);
}

/* Pop-up page. */

.owindow {
    background-color: var(--royalblue);
    border: none;
}
.owindow .title {
    cursor: auto;
    background-color: transparent;
    border-bottom: 0.2em solid var(--navyblue);
    text-align: right;
    font-family: var(--zolm);
    padding: 1em;
    text-transform: capitalize;
}
.owindow img {
    background-color: transparent !important;
    padding: 0 !important;
    border: var(--doubleking);
    margin-right: 1em;
}
.owindow td img {
    display: none;
}
.owindow .button-bar a {
    border-color: var(--navyblue);
    border-width: 0.2em;
    background-color: var(--royalblue);
}
.owindow .button-bar a:hover {
    background-color: var(--navyblue);
}

/* Account and search buttons. */

#account-options {
    margin-top: 3px;
    margin-right: 12px;
    text-align: center;
    border: none;
    border-color: transparent;
    background-color: var(--yellow);
    width: fit-content;
}
#account-topbutton {
    font-size: 80%;
    border: none;
    margin-left: 0;
    transition: 200ms;
}
#account-topbutton:hover {
    color: var(--royalpurple);
}
#account-options li a {
    color: var(--yellow);
    background-color: var(--royalblue);
    text-transform: capitalize;
    transition: 200ms;
}
#account-options li a:hover {
    color: var(--royalblue);
    background: var(--yellow);
    text-decoration: none;
}
#search-top-box {
    align-items: flex-start;
    flex-direction: row-reverse;
    flex-wrap: wrap;
    padding: 8px 12px 0 0;
    font-family: var(--zolm);
}
#search-top-box-form input[type=submit] {
    background-color: var(--yellow);
    background-image: none;
    color: var(--navyblue);
    border: 0.15em dotted var(--yellow);
    box-shadow: none;
    border-radius: 0;
    transition: 200ms;
}
#search-top-box-form input[type=submit]:hover {
    background-color: var(--navyblue);
    border: 0.15em dotted var(--yellow);
    background-image: none;
    color: var(--yellow);
    box-shadow: none;
}

#header {
grid-template-areas:
    ". . login login login"
    ". . . . search"
    "h1 h1 h1 h1 h1"
    "h2 h2 h2 h2 h2"
    ". . . . ."
    "top-bar top-bar top-bar top-bar top-bar";
    grid-template-columns: repeat(5, 1fr);
background-image: url(http://smlt.wikidot.com/local--files/fish-12:gold/mcdbanner.png), linear-gradient(10deg, var(--royalblue), var(--navyblue));
    background-size: contain;
    background-position: center;
    background-repeat: repeat;
}
#header h1, #header h2 {
    padding-inline-start: 0;
    text-align: center;
    position: relative;
    display: none;
}
#header h1::before {
    position: absolute;
    content: "";
    background-color: var(--sp_tab-text-color);
    clip-path: var(--diamond);
    top: 50%;
    left: 50%;
    z-index: 1;
    transform: translate(-50%, -50%);
    height: 10em;
    width: 10em;
}
#header h2::before, #header h1::after {
    display: none;
}
#header h1 a::before {
    font-family: var(--stix);
    background-image: var(--haze);
    color: transparent;
    background-clip: text;
    text-shadow: none;
    position: relative;
    z-index: 5;
}

/* Top bar. */

#top-bar {
    font-family: var(--teac);
    max-width: 100%;
    border-image: var(--gold);
    border-image-slice: 1;
    border-width: 0.2em;
    border-style: solid;
    border-left: 0px;
    border-right: 0px;
}
#top-bar ul li {
    flex-grow: 1;
}
#top-bar ul li a {
    transition: 200ms;
}
#top-bar div[class*="top-bar"] > ul {
    justify-content: center;
}
#top-bar ul li ul {
    border-width: 0 0;
    box-shadow: none;
    border-bottom: 0.2em solid var(--yellow);
}
#top-bar :is(.top-bar, .mobile-top-bar) ul li ul li a {
    line-height: 2;
    text-align: center;
}
#top-bar .open-menu a {
    border: 0.07em dotted var(--yellow);
    border-radius: 0;
    background-color: var(--yellow);
    color: var(--navyblue);
    transition: 200ms;
}
#top-bar .open-menu a:hover {
    background-color: var(--navyblue);
    color: var(--yellow);
}

#side-bar {
    background-color: rgba(0, 0, 0, 0.5);
}
#interwiki .heading, #side-bar .heading {
    color: mintcream;
    border-bottom: dotted 3px mintcream;
}
#side-bar .side-block.media, #side-bar .side-block.resources, #side-bar .side-block, #interwiki .side-block {
    background-color: var(--navyblue);
    border-radius: 0;
    box-shadow: none;
    border: solid 1px var(--royalblue);
}

blockquote, div.blockquote {
    border: solid 1px var(--yellow);
    background-color: var(--navyblue);
}

#container {
    background-image: linear-gradient(140deg, transparent 50.9vw, var(--navyblue) 50.9vw 51vw, transparent 51vw), linear-gradient(195deg, transparent 50.9vw, var(--navyblue) 50.9vw 51vw, transparent 51vw), linear-gradient(113deg, transparent 50.9vw, var(--navyblue) 50.9vw 51vw, transparent 51vw), linear-gradient(34deg, transparent 55.9vw, var(--navyblue) 55.9vw 56vw, transparent 56vw), linear-gradient(-30deg, transparent 55.9vw, var(--navyblue) 55.9vw 56vw, transparent 56vw);
}

#page-title {
    font-family: var(--stix);
    font-weight: bold;
    background-image: var(--gold);
    color: transparent;
    background-clip: text;
    font-size: clamp(1.5rem, 0.786rem + 1.905vw, 2.5rem);
    border-color: transparent;
    margin: auto;
    width: fit-content;
}
body {
    color: var(--grey);
    background-color: var(--royalblue);
    font-family: var(--robo);
}
a, a.newpage, a:visited, #interwiki a, #side-bar a {
    color: var(--yellow);
    transition: 200ms;
}
a:hover, a:visited:hover, #interwiki a:hover, #side-bar a:hover {
    color: var(--yellow);
    text-decoration: underline;
    text-decoration-style: dotted;
    text-underline-offset: 5px;
}
h1, h2, h3, h4, h5 {
    font-family: var(--zolm);
    color: var(--yellow);
}
h1::before, h2::before, h3::before, h4::before, h5::before, h1::after, h2::after, h3::after, h4::after, h5::after {
    background-color: var(--yellow);
    width: 1rem;
    height: 1rem;
    content: "";
    display: inline-block;
    margin-right: 0.2em;
    clip-path: var(--diamond);
}
h1::before, h2::before, h3::before, h4::before, h5::before {
    margin-right: 0.2em;
}
h1::after, h2::after, h3::after, h4::after, h5::after {
    margin-left: 0.2em;
}

.yui-navset.yui-navset-top .yui-nav :is(li, .selected) {
    flex: 100%;
}

.yui-navset .yui-nav {
    border-width: 0;
}
.yui-navset .yui-content {
    background-color: var(--royalblue);
}

.ornament {
    width: 20%;
    height: 20%
    background-image: url('http://scp-sandbox-3.wikidot.com/local--files/black-diamond-billions-hub/ornament2.png');
    background-repeat: no-repeat;
}

.last-tabview .yui-content > :nth-child(7) {
  max-height: 70vh;
  overflow: auto;
}

.window-discord {
    margin-left: 25%;
    margin-right: 25%;
    padding: 10px;
    border-image: linear-gradient(90deg, var(--yellow) 40%, transparent 40% 42%, var(--yellow) 42% 44%, transparent 44% 46%, var(--yellow) 46% 48%, transparent 48% 50%, var(--yellow) 50% 52%, transparent 52% 54%, var(--yellow) 54% 56%, transparent 56% 58%, var(--yellow) 58% 60%, transparent 60% 62%, var(--yellow) 62% 100%) 1;
    border-width: 4px;
    border-style: solid;
}

@media (max-width: 768px) {
  #header {
    background-size: cover;
  }
}

:root .authorbox {
  background-color: var(--royalblue);
  border: 1px solid var(--grey);
}

