[[include :scp-wiki-cn:theme:shivering-night low-height=*]]

[[module css]]
:root{
  --bg-img: url(https://scpsandboxcn.wikidot.com/local--files/blendcon/macau_nightscene2.jpg);
  --subtitle-content: "- BLENDCON, 2024 -";
  --blur-mode: none;

  --blend-text-color: #e0e3e0;
  --link-color: 36.28% 75.55% 75.05%;

  --main-content-width: 57.5rem;
  --avatar-size: 75px;
}

/* anchor */

a:link, a:visited {
    color: inherit;
    text-decoration: currentcolor solid underline;
}
a:hover, a:active {
    background-color: #FFF;
    color: #000;
    text-decoration: #111 solid underline;
}

.licensebox a:hover, .licensebox a:active {
    color: #000 !important;
}

a[href="#side-bar"] {
    text-decoration: none !important;
}

.listwork a:link, .listwork a:visited {
  color: var(--blend-text-color);
}

.listwork .three-lines tr>td:first-of-type a:link {
  background-color: rebeccapurple;
}

.listwork .three-lines tr>td:first-of-type a:visited {
  background-color: black;
}

.earthworm a:link, .earthworm a:visited {
    color: #fffef9 !important;
    text-decoration: currentcolor solid underline !important;
}

.earthworm a:hover, .earthworm a:active {
    color: #000107 !important;
    text-decoration: #111 solid underline !important;
}

/* three-line table from parallel */

div.table.three-lines {
    overflow-x: auto;
}

div.table.three-lines table.wiki-content-table {
    width: calc(100% - 0.2rem);
    border: solid var(--blend-text-color);
    border-width: 0.2rem 0;
}

div.table.three-lines table.wiki-content-table :is(th, td) {
    border: none;
    color: inherit;
    background-color: transparent;
}

div.table.three-lines table.wiki-content-table tr:nth-child(1) th {
    border-bottom: solid 0.1rem var(--blend-text-color);
}

.yui-content div.table.three-lines table.wiki-content-table tr td:nth-child(2) p { margin: 0; }

/* other items */

#page-content hr {
    background-color: var(--blend-text-color);
}

h1,h2,h3,h4,h5,h6 {text-align: center;}
li:not(:first-child) {margin-top: 0.75em;}
li > ul {margin-top: 0.75em;}
li > br {display: none;}

ul, ol {padding-left: 10px;}

.cent {text-align: center;}

/* dialogs */

.dialog.right {
  position: relative;
  margin-left: auto;
  margin-right: 1.25em;
  margin-top: 2em;
  width: 40%;
  border-radius: 8px;
  border: 2px dotted rgb(var(--link-color));
}

.dialog.right .avatar {
  position: absolute;
  left: calc(100% + 0.5em);
  top: calc(1.5em - var(--avatar-size));
  width: var(--avatar-size);
  height: var(--avatar-size);
}

.dialog.left {
  position: relative;
  margin-left: 1.25em;
  margin-right: auto;
  margin-top: 2em;
  width: 40%;
  border-radius: 8px;
  border: 2px dotted rgb(var(--link-color));
}

.dialog.left .avatar {
  position: absolute;
  left: calc(-0.5em - var(--avatar-size));
  top: calc(1.5em - var(--avatar-size));
  width: var(--avatar-size);
  height: var(--avatar-size);
}

.dialog .avatar img {width: var(--avatar-size);}

.dialog > img {
  margin: 1em auto;
}

.dialog.aic {
  border: 4px double #dcd5d7;
}

@media (max-width: 1024px) {
  .dialog.right, .dialog.left {
    margin-left: 1.25em;
    margin-right: 1.25em;
    width: unset;
    margin-top: 1em;
  }
  .dialog.right .avatar {
    left: calc(100% - var(--avatar-size));
    top: calc(-0.25em - var(--avatar-size));
  }
  .dialog.left .avatar {
    left: 0;
    top: calc(-0.25em - var(--avatar-size));
  }
  .dialog.aic {
    margin-top: calc(1.25em + var(--avatar-size))
  }
}

/* HAS CHECK */

.author-warning {display: none;}
@supports not selector(:has(.ex-author)) {
  .author-warning {display: block;}
}

.co-author:has(:not(p, br)) {
  display: none;
}

#wiki-tab-0-1 div.footnotes-footer {
  display: none;
}

/* specified for hub */

a[href="http://www.wikidot.com/user:info/basalt-highlighter"]:nth-of-type(2)::after {
  content: ".aic";
}

#page-content span.avatarhover {
  margin-left: 1px;
  margin-right: 1px;
}

#page-content .avatarhover img {
  background-image: none !important;
  padding: 1px !important;
}

strong {color: rgb(var(--link-color));}

#page-content td {
  text-align: center !important;
  min-width: 7em;
}
span.article-title:not(:empty)::before {
  content: " - ";
  font-weight: bold;
}
div.wine {
  text-align: right;
  color: #c1651a;
  width: 77%;
  margin-left: 20%;
}
#page-title {
  text-align: center;
  margin-top: 0.5em;
}

div#page-content {font-size: 108%;}

.yui-navset .yui-nav .selected a em {background-color: #007171;}
.yui-navset .yui-content {border-top-color: #007171;}

.meta-displayer {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  text-align: center;
  align-items: center;
  margin: 0.75em 2em;
  column-gap: 1em;
}

@media (max-width: 1024px) {
  div#page-content {font-size: unset;}
  .meta-displayer {margin: 0.75em 0.25em;}
}

.hidden-ach tr > td:nth-of-type(2) .hint {display: block;}
.hidden-ach tr > td:nth-of-type(2) .answer {display: none;}

.hidden-ach tr > td:nth-of-type(2):hover .hint {display: none;}
.hidden-ach tr > td:nth-of-type(2):hover .answer {display: block;}

.hidden-ach tr > td:nth-of-type(2) {width: 70%;}

/* 结算页面 */

.colmod-link-top {
  text-align: center;
}

.foldable-list-container a {
  color: rgb(var(--link-color));
  font-weight: bold;
  font-size: 144%;
  font-family: var(--title-font);
}

.award-title {
  font-weight: bold;
  font-size: 144%;
  font-family: var(--title-font);
  text-align: center;
}

.award-title p {
  margin-bottom: 0.4rem;
}

.author {
  margin-left: 50%;
}

.author p {
  margin-top: 0.4rem;
}

.other-works {
  margin-left: auto;
  margin-right: auto;
  width: fit-content;
  font-size: 80%;
}

.trigger .short {
  display: inline;
  text-decoration: underline;
}
.trigger .long {display: none;}
.trigger:hover .short {display: none;}
.trigger:hover .long {display: inline;}

[[/module]]

@@ @@

