.birddy {
  height: 80px;
  position: fixed;
  z-index: 9;
  bottom: 0;
  left: calc(mod(%%rating%%, 10) * 10% + 5%);
  -webkit-animation: udmove 20s linear forwards infinite;
  animation: udmove 20s linear forwards infinite;
}

