[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Cairo&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300;400;700;900&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300&display=swap');

/* 图源：https://pixabay.com/zh/photos/future-eye-robot-eye-machine-175620/ */

div#page-content {
    font-size: 0.9375rem;
    font-family: "Noto Sans SC", sans-serif;
    line-height: 160%;
}

div#page-title {
    display: block;
}

blockquote,
.blockquote {
    border-radius: 1rem;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/trap/tech_g.svg");
    background-size: 2rem auto;
    background-position: 0.5rem 0.5rem;
    background-repeat: no-repeat;
}

.blockquote.notice {
    border-radius: 0;
    background-image: none;
}

blockquote h2,
.blockquote h2 {
    text-align: center;
}

div.page-container {
    position: relative;
    overflow: hidden;
    border: solid 1px #888;
}

div.faketopbar {
    position: absolute;
    left: 0;
    top: 0;
}

div.page-container.page-1 {
    background-color: #0F0;
    color: #00F;
}

div.page-container.page-1 h1 {
    font-size: 25vw;
    line-height: 10vw;
    font-weight: 900;
    color: #F00;
    margin: 0;
    text-stroke: 4px #F0F;
    -webkit-text-stroke: 4px #F0F;
    paint-order: stroke fill;
    -webkit-paint-order: stroke fill;
    max-height: 200px;
}

div.fakeHeaderContainer {
    height: 200px;
    background-size: 100px 400px;
    background-position: center top;
    background-repeat: repeat-x;
    background-color: #FFF;
    background-image: url("http://scp-wiki.wdfiles.com/local--files/theme%3Ascp-trans-pride/Trans-CSS-File.png");
}

div.fakeHeader {
    margin: 0 auto;
    background: url('https://scp-wiki-cn.wdfiles.com/local--files/component%3Atheme/logo.png') 10px 40px no-repeat;
    background-size: 100px 100px;
    height: 141px;
    position: relative;
}

div.fakeHeader h1 {
    margin: 0;
    margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;
}

div.fakeHeader h2 {
    margin: 0;
    margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

div.fakeHeader h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0;
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi, Simhei, Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}

div.fakeHeader h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* Mobile Media Query */
@media (max-width: 479px) {
    div.fakeHeader {
        max-width: 90% !important;
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }

    div.fakeHeader h1 a {
        font-size: 100%;
    }

    div.fakeHeader h1, div.fakeHeader h2 {
        margin-left: 66px;
    }
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {
    div.fakeHeader {
        max-width: 90% !important;
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }

    div.fakeHeader h1 a {
        font-size: 120%;
    }

    div.fakeHeader h1, div.fakeHeader h2 {
        margin-left: 80px;
    }
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) {
    div.fakeHeader {
        max-width: 90% !important;
        background-position: 1em 4em;
        background-size: 77px 77px;
    }

    div.fakeHeader h1 a {
        font-size: 140%;
    }

    div.fakeHeader h1, div.fakeHeader h2 {
        margin-left: 93px;
    }
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
    div.fakeHeader {
        max-width: 720px !important;
        background-position: 1em 4em;
        background-size: 88px 88px;
    }

    div.fakeHeader h1 a {
        font-size: 160%;
    }

    div.fakeHeader h1, div.fakeHeader h2 {
        margin-left: 106px;
    }
}

div.page-container.page-2 {
    background-color: #F0F;
    background-image: none;
}

div.page-container.page-2 h1 a,
div.page-container.page-2 h2 span {
    color: #0DD;
    font-family: "Noto Serif SC";
    text-shadow: -3px 3px #0FF, 3px -3px #F00;
}

div.page-container.page-2 div.fakeHeader {
    background-image: url("https://scpsandboxcn.wikidot.com/local--files/collab:shunengshengqiao/logo1.png");
}

div.page-container.page-2 div.mask {
    position: absolute;
    left: -75%;
    top: 0;
    backdrop-filter: invert(100%);
    -webkit-backdrop-filter: invert(100%);
    transform: rotate(45deg);
    -webkit-transform: rotate(45deg);
    max-width: unset !important;
    width: 200%;
    height: 50%;
}

div.page-container.page-3 div.fakeHeader {
    background-image: url(http://scp-wiki.wdfiles.com/local--files/theme%3Atechnoblast/logoaiad.png);
    background-size: 7.3rem;
    background-position: 3px 29px;
}

@media (max-width: 767px) and (min-width: 480px) {
    div.page-container.page-3 div.fakeHeader {
      background-size: 6rem;
      background-position: -4px 44px;
    }
}

@media (max-width: 479px) {
    div.page-container.page-3 div.fakeHeader {
      background-size: 4.6rem;
      background-position: -3px 58px;
    }
}

div.page-container.page-3 div.fakeHeader h1  span a {
    font-family: 'Orbitron', '黑体', sans-serif;
    font-weight: bold;
    font-size: 0.9em;
    color: #00ffff;
    text-shadow: 0px 0px 0px #000;
}

div.page-container.page-3 div.fakeHeader h2 span {
    font-family: 'Cairo', '等线', sans-serif;
    font-weight: bold;
    font-size: 1.2em;
    color: #ff9944;
    padding: 17px 0;
    text-shadow: 0px 0px 0px #000;
    white-space: pre;
}

div.page-container.page-3 div.mask {
    position: absolute;
    left: 0;
    top: 0;
    backdrop-filter: invert(100%);
    -webkit-backdrop-filter: invert(100%);
    width: 100%;
    height: 100%;
    mask-image: linear-gradient(45deg, transparent 0%,  #000 0%, #000 25%, transparent 25%, transparent 50%, #000 50%, #000 75%, transparent 75%, transparent 100%);
    mask-size: 0.5rem 0.5rem;
    mask-repeat: repeat;
    -webkit-mask-image: linear-gradient(45deg, transparent 0%,  #000 0%, #000 25%, transparent 25%, transparent 50%, #000 50%, #000 75%, transparent 75%, transparent 100%);
    -webkit-mask-size: 0.5rem 0.5rem;
    -webkit-mask-repeat: repeat;
}

div.page-container.page-4 div.mask {
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    top: 0;
    margin: auto;
    width: 100%;
    height: auto;
}
[[/module]]

