[[module css]]
h1, #page-title {
    color: #ed8f07;
}
#page-title {
    border-color: #15486b;
}
a {
    color: #478bad;
}
a.newpage {
    color: #478bad;
}
a:visited {
    color: #478bad;
}
a:hover {
    color: #ed8f07;
}
body {
    color: #15486b;
}

div#container-wrap {
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/margin.png);
    background-repeat: repeat-x;
}

#header {
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/breadlogo.png);
    background-repeat: no-repeat;
}
#search-top-box-input {
    color: #0c4d66;
    background-color: #0c4d66;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #fff;
    background-color: #0c4d66;
}
#search-top-box-form input[type=submit] {
    border-color: #999;
    color: #478bad;
    background-color: #0c4d66;
    background: linear-gradient(to bottom, #225c7c, #15486b, #0b3851);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #f0fcff;
    color: #f0fcff;
    background-color: #225c7c;
    background: linear-gradient(to bottom, #225c7c, #15486b, #0b3851);
}

#login-status {
    color: #15486b;
}
#login-status a {
    color: #15486b;
}
#login-status ul a {
    color: #15486b;
}
#account-topbutton {
    background-color: #f0fcff;
    color: #0b3851;
}

#header h1 a {
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
#header h1 a::before {
    content: "ブレッド君君君";
    white-space: nowrap;
    color: #c9f2ff;
    text-shadow: 3px 3px 5px #0c4d66;
}
#header h2 span {
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
    user-select: none;
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
}
#header h2 span::before {
    content: "　食べても良いけど、急かすのはやめてちょ！　";
    white-space: nowrap;
    color: #478bad;
    text-shadow: 1px 1px 1px #0c4d66;
}

/* TOP MENU */
#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    color: #15486b;
}
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background-color: #fce98b;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #225c7c;
}

/* SIDE MENU */
#side-bar a {
    color: #478bad;
}
#side-bar a:visited {
    color: #478bad;
}
#side-bar a:hover {
    color: #ed8f07;
}
#side-bar .side-block {
    border-color: #0c4d66;
    background-color: #f0fcff;
    box-shadow: 0 2px 6px rgba(12,77,102,.5);
}
#side-bar .heading {
    color: #225c7c;
    border-color: #2e2930;
}
#side-bar div.menu-item .sub-text {
    color: #225c7c;
}

#side-bar .collapsible-block-folded {
    background-image: none;
}
#side-bar .collapsible-block-unfolded-link {
    border-color: #522f60;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #c9f2ff;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #225c7c;
}

/* YUI-TABS */
.yui-navset .yui-content{
    background-color: #dae6e8;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #94c7d3;
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/colorrr.png);
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-color: #0b3851;
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/colorrr.png);
    color: #478bad;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-color: #efb32e;
    background-image: url(http://scp-sandbox-3.wikidot.com/local--files/breaddddd/colorrr.png);
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #ed8f07;
}

/* Page Rating Module Customizations */
div.page-rate-widget-box .rate-points {
  background-color: #efb32e;
  border-color: #ed8f07;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  background-color: #fce98b;
  border-color: #ed8f07;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  background-color: transparent;
  color: #ed8f07 ;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  background-color: #ed8f07;
  color: #fce98b;
}
.page-rate-widget-box .cancel {
  background-color: #ed8f07;
  border-color: #ed8f07;
}
.page-rate-widget-box .cancel a {
  background-color: transparent;
  color: #966;
}
.page-rate-widget-box .cancel a:hover {
  background-color: #ed8f07;
  color: #fce98b;
}

.scp-image-block {
    border-color: #fce98b;
}

/* off-canvas */

@media (max-width: 767px) {
#side-bar {
    background-color: transparent;
}
}
