[[module CSS]]
@import url("https://use.typekit.net/tqr1skr.css");
@import url('https://fonts.googleapis.com/css?family=Playfair+Display:700|Poppins:400,600&display=swap');
@import url("https://fontlibrary.org/face/comic-neue");

/* WANDERERS' CSS */
#page-content .border{
    box-sizing: border-box;
    border: 2vw solid rgba(0,0,0,0.5);
    border-image: url(http://wanderers-library.wdfiles.com/local--files/component%3Adustjacket-theme/wl_border.png) 600 round;
    border-image-width: 6;
    border-radius: 3vw;
    background-color: rgb(45, 70, 45);
    padding: .85em 0;
    color: #FFDB5A;
}
.h2{
    margin: .43em 0 .34em;
    padding: 0;
    letter-spacing: 1px;
    font-size: 150%;
    font-family: 'adelle-sans', sans-serif;
    word-break: unset;
}

/* VOID */
div.void{
    border: 1px solid lightgray;
    background-color: #fff;
    box-shadow: 2px 2px 2px 2px rgba(0,0,0,.1);
    width: 24.67em;
    padding: 0 0 0 .85em;
    margin: .85em auto;
}
@media (max-width: 479px){
    div.void{ width: auto; margin: .85em 0; }
}
@media (min-width: 480px) and (max-width: 580px){
    div.void{ width: auto; margin: .43em; }
}
.void-dp{
    float: left;
    background-color: #fff;
    padding: 0 .85em;
    margin: .4em 0 0 .34em;
}
.void-activity{ color: blue; }

/* REDDIT */
.reddit-post{
    background-color: #fcfcfb;
    border: 1px solid #b5b3ac;
    padding: 0 .85em;
    margin: .43em;
    overflow: hidden;
    font-family: verdana,arial,sans-serif;
}

/* PITCHFORK */
.pitchfork-header{
    font-family: 'Playfair Display', serif;
    font-size: 260%;
    font-weight: 700;
    line-height: 38.1px;
}

/* dado */

div.dado{
   color: #000000;
   font-family: 'ComicNeueAngularRegular', 'MS Comic Sans', 'Comic Sans', sans-serif;
   padding: 0;
   font-size: 1.5em;
}
[[/module]]


