@import url("https://use.typekit.net/tqr1skr.css");
@import url('https://fonts.googleapis.com/css?family=Playfair+Display:700|Poppins:400,600&display=swap');
@import url("https://fontlibrary.org/face/comic-neue");

/* WANDERERS' CSS */
.border{
    box-sizing: border-box;
    padding: .85em 0;

    color: #FFDB5A;
    background-color: rgb(45, 70, 45);
    border: 2vw solid rgba(0,0,0,0.5);
    border-radius: 3vw;
    border-image: url(https://wanderers-library.wdfiles.com/local--files/component%3Adustjacket-theme/wl_border.png) 600 round;
    border-image-width: 6;
}
.border a{
    padding-bottom: 2px;

    color: #FFDB5A;
    border-bottom: 1px solid #FFDB5A;

    text-decoration: none;
}
.border a:hover{
    border-bottom: 2px solid #FFDB5A;

    font-weight: bold;
    letter-spacing: 0;
    text-decoration: none;
}
.h2{
    margin: .43em 0 .34em;
    padding: 0;

    font-size: 150%;
    font-family: 'adelle-sans', sans-serif;
    letter-spacing: 1px;
    word-break: unset;
}

/* REDDIT */
.reddit-post{
    margin: .43em; 
    padding: 0 .85em;

    background-color: #fcfcfb; 
    border: 1px solid #b5b3ac; 

    font-family: verdana,arial,sans-serif;
    overflow: hidden;
}

/* PITCHFORK */
.pitchfork-header{
    line-height: 38.1px;
    font-size: 260%; 
    font-family: 'Playfair Display', serif; 
    font-weight: 700; 
}

/* dado */
div.dado{
   padding: 0;

   color: #000;
   font-size: 150%;
   font-family: 'ComicNeueAngularRegular', 'MS Comic Sans', 'Comic Sans', sans-serif;
}

/* BLOCKQUOTE LINK FIX */
 blockquote a, div.blockquote a{
     color: hsl(9, 78%, 33%);
}
 blockquote a:hover, div.blockquote a:hover, 
 blockquote a:active, div.blockquote a:active,
 blockquote a:visited, div.blockquote a:visited{
    color: hsl(11, 82%, 23%);
}
[[/module]]

