[[module CSS]]
#page-title {display: none;}

body {
    background: #FFFAF0;
    color: #000;
}
div#container-wrap {
    background: none;
}
#header {
    background: none;
    text-align: center;
    margin: 0;
    max-width: unset;
    width: 100%;
}
#header h1 {
    float: unset;
    height: 100%;
    margin: 0;
    position: relative;
    bottom: 0;
    max-width: none;
}
#header h1 a {
    content: "弟の食料品";
    align-items: center;
    box-shadow: 0 0.25em 2em;
    color: #000;
    display: flex;
    font-family: sans-serif;
    font-weight: 100;
    height: 100%;
    justify-content: center;
    line-height: 1;
    max-height: unset;
    padding: 0;
    text-shadow: 0 0 0.2em;
    -webkit-box-reflect: below 0 linear-gradient(rgba(0,0,0,0),rgba(0,0,0,.5));
}
#header h1 a::before {
    content: "弟の食料品";
    width: 150%;
}

#header h1 a span{
display: block;
content: "";
visibility: hidden;
}

#header h2 {
    display: none;
}

[[/module]]

[[module CSS]]
.yui-navset .yui-content{
padding: 40px;
border-width: 0 0 2px 0;
background: #fcfcfc;
}

.yui-navset .yui-nav{
position: relative;
border: none;
height: 2em;
}

.yui-navset .yui-nav::after{
content: "";
position: absolute;
width: 100%;
height: 100%;
pointer-events: none;
background: linear-gradient(to right, #fcfcfc 2%, rgba(252, 252, 252, 0) 20%, rgba(252, 252, 252, 0) 80%, #fcfcfc 98%);
}

.yui-navset .yui-nav li{
overflow-x: hidden;
transition-duration: 0.5s;
position: absolute;
width: 20%;
height: 2em;
left: 0;
padding: 0;
text-align: right;
}

.yui-navset .yui-nav li.selected{
width: 60%;
left: 20%;
text-align: left;
}

.yui-navset .yui-nav li.selected + li{
left: 80%;
text-align: left;
}

.yui-navset .yui-nav li.selected + li ~ li{
width: 0;
left: 100%;
text-align: left;
}

.yui-navset .yui-nav li a{
width: 100%;
height: 100%;
border-width: 1px 1px 0 1px;
padding: 0;
box-sizing: border-box;
}

.yui-navset .yui-nav li.selected a em {
padding: 0.25em .75em;
}

.yui-navset .yui-nav li a em {
top: 0;
border: none;
overflow: hidden;
white-space: nowrap;
}

[[/module]]

