}

.yui-navset .yui-nav li.selected{
flex-grow: 5;
margin: 0;
}

.yui-navset .yui-nav li.selected + li{
flex-grow: 1;
}

.yui-navset .yui-nav li a{
width: 100%;
border: 0;
}

.yui-navset .yui-nav li.selected a em {
padding: 0.25em .75em;
}

.yui-navset .yui-nav li.selected + li a em{
display: none;
}

.yui-navset .yui-nav li.selected + li a::before{
content: "次へ>";
width: 100%;
display: block;
border: solid #a3a3a3;
border-width: 1px 0 0;
cursor: hand;
padding: 0.25em .75em;
left: 0;
right: 0;
bottom: 0;
top: -1px;
position: relative;
}
[[/module]]
