[[/module]]
[[div_ class="kekkon-area"]]
[[html]]
<style type="text/css">
html, html#html-block-html body{
height: 100%;
pointer-events: none;
}
#area{
width: 100%;
height: 100%;
pointer-events: none;
}
.kekkon{
animation: fade 12s linear both;
}

@keyframes fade{
0%{opacity: 1;}
70%{opacity: 1;}
95%{opacity: 0;}
100%{opacity: 0;}
}
</style>

<div id="area">
</div>
<script type="text/javascript">
let area = document.getElementById("area");
let width = area.clientWidth;
let height = area.clientHeight;

let walkdelay = 500;
let removedelay = 12000;

let elem = document.createElement("div");
elem.classList.add("kekkon");
elem.style.position = "absolute";
elem.style.transformOrigin = "50% 50%";
elem.style.backgroundSize = "contain";
elem.style.backgroundImage = 'url("http://scp-jp-storage.wdfiles.com/local--files/file%3A4549537-6-9bgy/R2O4ZUm2nJaEJKM1608197283_1608197299.png")';

setTimeout(addKekkon, 1000);

window.addEventListener("resize", function(){
    width = area.clientWidth;
    height = area.clientHeight;
});


function addKekkon(){
    let cloneelem = elem.cloneNode(true);
    let size = Math.random() * 35 + 15;
    cloneelem.style.width = size + "px";
    cloneelem.style.height = size + "px";
    cloneelem.style.transform = "rotate(" + (Math.random() * 360) + "deg)";
    cloneelem.style.top = (Math.random() * height) + "px";
    cloneelem.style.left = (Math.random() * width) + "px";
    area.appendChild(cloneelem);
    setTimeout(addKekkon, walkdelay);
    setTimeout(deleteKekkon, removedelay, cloneelem);
}

function deleteKekkon(elem){
    elem.remove();
}
</script>
[[/html]]
[[/div]]

