[[module css]]
.useCursor {
    text-align: center;
}
.useCursor p {
   display:inline-block;
}
.cursor {
    position: relative;
    width: 1px;
    height: 12px;
    background-color: gray;
    animation-name: blink;
    animation-duration: 800ms;
    animation-iteration-count: infinite;
    opacity: 1;
    display:inline-block;
}
@keyframes blink {
    from { opacity: 1; }
    to { opacity: 0; }
}
[[/module]]
[[module CSS]]
.email {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/project-scarlet-dawn/01.png);
    background-position: top 7.5px right 7.5px;
    background-size: 70px;
    background-repeat: no-repeat;
    border: solid 3px #B30030;
    padding: 1px 15px;
    margin: auto;
    margin-top: 10px;
    margin-bottom: 10px;
    width: 85%;
}

.pentagram {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/project-scarlet-dawn/ASASAS.png);
    border-color: #0a0037;
    --hyperlink: #404d7e;
    --hyperlink-visited: #0a0037;
}

.email hr {
    border: 0;
    border-top: 2px solid #B30030;
    margin: 1em;
    padding: 0;
    display: block;
    height: 0;
}

.pentagram hr {
    border-color: #0a0037;
}
table {
    --border-colour: #B30030;
}

table.wiki-content-table th {
    background-color: #d7003a;
    color: #ffffff;
}
[[/module]]

[[module css]]
.mail-main .blockquote {
  border: 1px solid black;
  background: none;
  margin: 2rem 0;
}

@media (max-width: 1024px) {
  :is(.blockquote, blockquote) :is(.blockquote, blockquote) { padding-inline: 0.5rem;}
}

div.mail-file.not-bot { margin-bottom: 0.5rem; }
div.mail-file.not-top { margin-top: 0.5rem; }

div.light div.logo {
  background-blend-mode: lighten;
}

div.dark div.logo {
  background-blend-mode: darken;
}

table.xls {
  margin-top: 2rem;
  border: #e0e0e0 solid 1px;
  border-collapse: collapse;
  width: 100%;
  font-family: 'ç­‰çº¿', 'Microsoft YaHei', 'å¾®è½¯é›…é»‘', sans-serif;
  overflow-x: auto;
}

table.xls th, table.xls td {
  border: #e0e0e0 solid 1px;
  padding: 2px;
  text-align: center;
}

table.xls th, table.xls tr td:first-of-type {
  background-color: #f0f0f0;
  font-weight: normal;
}

table.xls tr td:first-of-type {
  width: 1.5em;
}

