@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@600&amp;display=swap');

:root {
/* 字体修正 */
--body-font: "Noto Sans SC", sans-serif;
--header-font: "Noto Sans SC", sans-serif;
--title-font: "Noto Sans SC", sans-serif;
--mono-font:"Noto Serif SC","ZCOOL QingKe HuangYou","Andale Mono","Courier New",Courier,monospace;

/* logo修正 */
--logo-image:url(http://skynight-sandbox.wdfiles.com/local--files/component%3Ablack-highlighter-theme/logo.svg);

/* 颜色修正 */
--black-monochrome: 0, 0, 0;
--white-monochrome: 255, 255, 255;
}

/* Headers */

h1,
h2,
h3,
h4,
h5,
h6 {
font-family: "Poppins", sans-serif;
font-family: var(--title-font);
}

h1 {
color: rgb(12, 12, 12);
color: rgb(var(--black-monochrome));
font-weight: 700;
margin: 0.7em 0 0.6em;
padding: 0 0 0.25em;
}

h2,
h3,
h4,
h5,
h6 {
font-weight: 900;
letter-spacing: 0.063rem;
margin: 0.5em 0 0.4em;
padding: 0;
}

ul {
list-style: square;
}
[[/module]]

[[include :scp-wiki:component:fade-in speed=1]]
[[module css]]
/*雾气*/
:root {
  --sky-at-dawn: rgba(255, 255, 255, 0);
  --sky-at-morning: rgba(255, 255, 255, 0);
  --sky-at-daytime: rgba(255, 255, 255, 0);
  --sky-at-evening: rgba(255, 255, 255, 0);
  --sky-at-dusk: rgba(255, 255, 255, 0);
  --sky-at-night: rgba(255, 255, 255, 0);
  --sky-length: 15;
  --sky-time: 60s;
  --gradient-header: linear-gradient(transparent, transparent);
  --diagonal-stripes: linear-gradient(transparent, transparent);
}

body::before,
div#container-wrap-wrap::before,
div#container-wrap::before,
div#container-wrap-wrap::after,
body::after {
  content: "";
  position: absolute;
  height: var(--header-height-on-desktop); width: 100%; top: 0; left: 0;
  pointer-events: none;
}

body::before {
  background: linear-gradient(
                to top,
                rgb(var(--sky-at-morning)) 0%,
                rgb(var(--sky-at-morning)) 11%,
                rgb(var(--sky-at-daytime)) 22%,
                rgb(var(--sky-at-daytime)) 33%,
                rgb(var(--sky-at-evening)) 44%,
                rgb(var(--sky-at-dusk)) 55%,
                rgb(var(--sky-at-night)) 66%,
                rgb(var(--sky-at-night)) 77%,
                rgb(var(--sky-at-dawn)) 88%,
                rgb(var(--sky-at-morning)) 100%
              );
  background-size: 100% calc(var(--header-height-on-desktop)
                             * var(--sky-length));
  animation: sky linear var(--sky-time) infinite;
}
@keyframes sky {
  from { background-position: 0 0; }
  to {
    background-position: 0 calc(var(--header-height-on-desktop)
                                * var(--sky-length));
  }
}

div#container-wrap-wrap::after, body::after {
  background-size: var(--ashes-width) var(--header-height-on-desktop);
  background-repeat: repeat-x;
  animation: ashes-move linear var(--ashes-move-duration),
             ashes-fade ease calc(var(--sky-time) * 3);
  animation-iteration-count: infinite;
  animation-fill-mode: both;
}
div#container-wrap-wrap::after {
  background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Aswirling-ashes/ashes-large.png');
  --ashes-move-duration: 27s;
  --ashes-width: 500px;
  --ashes-opacity: 0.5;
}
body::after {
  background-image: url('https://scp-wiki.wdfiles.com/local--files/theme%3Aswirling-ashes/ashes-small.png');
  --ashes-move-duration: 20s;
  --ashes-width: 800px;
  --ashes-opacity: 0.4;
}
@keyframes ashes-move {
  from { background-position: 0 0; }
  to { background-position: var(--ashes-width) 0; }
}
@keyframes ashes-fade {
  0%, 85%, 96%, 100% { opacity: var(--ashes-opacity); }
  88.66%, 92.33% { opacity: 0.1; }
}

/*间隔*/

/*#skrollr-body {
    width: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background-image: url(http://scpsandboxcn.wdfiles.com/local--files/collab%3Amayca/M%E9%95%BF%E5%9B%BE%E6%94%B92);
