[[module css]]
:root {
    --activity-color: #22b322;
    --large-color: #ffc902;
}

#page-title { text-align: center; }

.canon-block.opener {
    font-weight: 550;
    font-size: 1.15em;
    padding-bottom: 0.375rem;
    margin-block: 1.25rem;
    background-color: #f8f8f8;
}

.info-wrapper {
    display: grid;
    grid-template-areas:
        "toc info"
        "toc legend";
    grid-template-columns: 13.5rem 1fr;
    grid-template-rows: auto 1fr;
    gap: .325rem .875rem;
    font-size: .9125em;
    margin-block: .5rem;
}

@media screen and (max-width: 37.5rem) {
.info-wrapper {
    grid-template-areas:
        "info"
        "toc"
        "legend";
    grid-template-columns: 1fr;
}
}

.info {
    grid-area: info;
    padding: 0 .75rem;
}

.info-wrapper > table,
.info-wrapper > table :is(tbody, tr, td) {
    display: contents;
}
#toc {
    grid-area: toc;
    display: grid;
    grid-template-areas:
        "title act"
        "list list";
    grid-template-columns: 1fr 4.5em;
    grid-template-rows: auto 1fr;
    margin: initial;
    padding: 0;
    box-sizing: border-box;
    border: none;
    background: transparent;
}

#toc #toc-action-bar {
    grid-area: act;
    border-bottom: solid 0.05rem #ccc;
    padding: 0.5rem 0;
    text-align: end;
}
#toc-list {
    grid-area: list;
    margin: 0;
    counter-reset: toc-index;
}
#toc-list > div {
    display: grid;
    grid-template-columns: 1.5rem 1fr;
    gap: .25rem;
    align-items: baseline;
    margin-left: 0!important;
    margin-block: .45em;
    line-height: 1.15;
}
/*
#toc-list > div:last-child {
    font-style: italic;
}
*/
#toc-list > div::before {
    counter-increment: toc-index;
    content: counter(toc-index) ".";
    font-size: .75em;
    text-align: end;
    font-feature-settings: 'case','ss01','ss04','tnum';
}
#toc-list > div > a {
    grid-column: 2/3;
    width: fit-content;
}

#toc .title {
    grid-area: title;
    margin: 0;
    padding: 0.5rem .175rem;
    border-bottom: solid 0.05rem #ccc;
    text-align: start;
}
.legend { grid-area: legend; }
.legend table { width: 100%; margin: 0;}
.legend table tr:first-child {
    border-bottom: solid 0.05rem #ccc;
}
.legend table td {
    border: none;
    padding: .625rem .325rem;
}
.legend table tr:first-child td {
    font-weight: bold;
    padding-inline: .175rem;
}

.act-marker, .vact-marker, .large-marker {
    display: block;
    position: relative;
    margin: auto;
    color: transparent;
    font-size: 0.1rem;
}
.act-marker, .vact-marker {
    height: .625rem; width: .625rem;
    transform: rotate(45deg);
    background-color: var(--activity-color);
}
.act-marker {
    background-color: transparent;
    box-sizing: border-box;
    border: solid 0.175rem var(--activity-color);
}
.large-marker {
    height: 1.125rem; width: 1.125rem;
    background-color: var(--large-color);
    clip-path: polygon(50% 0, 60% 40%, 100% 50%, 60% 60%, 50% 100%, 40% 60%, 0 50%, 40% 40%);
}

/*----------------------------------*/

#page-content hr {
    margin: 1.5rem 0;
    background-color: #ccc;
}

/*----------------------------------*/

.canon-wrapper {
    display: grid;
    gap: 1.25rem;
    margin-block: 1.25rem;
    clear: both;
}

.canon-block {
    --link-color: #b01;
    font-size: 1.05em;
    text-align: center;
    font-weight: bold;
    position: relative;
    padding: 1.125rem 1.675rem;
    background-color: #fff;
    box-shadow: 0 0 .325rem rgb(0 0 0 / 0.25);
}
.canon-block :is(h1,h2) {
    font-size: 1.75em;
    font-weight: bold;
    letter-spacing: initial;
    margin: 0.225em 0 0;
}

.canon-block a {
    color: var(--link-color);
}
.canon-block .snippet {
    text-align: initial;
    font-weight: normal;
    font-style: italic;
    font-size: .875em;
    line-height: 1.375;
    border-top: solid 0.05rem currentColor;
}
.canon-block .snippet p { line-height: inherit; }
.canon-block .snippet .source {
    font-size: .85em;
    font-style: initial;
    font-weight: 600;
    text-align: center;
}

.canon-block .list-pages-box { display: contents; }
.canon-block .activity-container {
    display: flex;
    align-items: center;
    gap: .675rem;
    width: 100%;
    padding-inline: .825rem;
    box-sizing: border-box;
    position: absolute;
    top: 1.125rem; left: 0;
    transform: translateY(-50%);
    pointer-events: none;
}

.activity-container :is(span,br) {
    display: none;
    margin: unset;
    pointer-events: auto;
}
.activity-container.active:not(.very-active) .act-marker { display: block; }
.activity-container.very-active .vact-marker { display: block; }

.large .activity-container .large-marker {
    display: block;
    transform: translateX(-.25rem);
}

/*----------------------------------*/

.canon-wrapper.sub {
    grid-template-columns: repeat(auto-fill,minmax(17.25rem,1fr));
    gap: 0.75rem;
}

.sub .canon-block {
    font-size: 0.825em;
    font-weight: normal;
    padding: 0.5rem 0.675rem;
}
.sub h2 { font-size: 1.5em; }

.sub .canon-block p {
    margin-block: .525em;
}

/*----------------------------------*/

.sywbac {
    background-color: #fff8cd;
    text-align: start;
    font-weight: normal;
    font-size: 1em;
}
.sywbac :is(.collapsible-block-folded, .collapsible-block-unfolded-link) {
    font-weight: bold;
    text-align: center;
}
[[/module]]
