%%content%%

[[module css]]
.infobox {
padding:0;
background-color:#222; 
border-radius:7px; 
float:right;
}

.infobox2 {
background-color:#0d0d0d;
color:#ffffff; 
font-size:120%;
border-radius:4px; 
text-align:center;
}

.infobox2 p {
margin: 0 auto;
width: 10em;
}

table.infobox {
min-width: 256px;
width: 30%;
}

table.infobox tbody tr td, table.infobox tbody tr th {
color: #fff;
}

.scp-list p {
margin: 0.25em;
}

.embedvideo {
background-color: #000;
color: #fff;
border: 1px solid #555555;
margin: 0.5em 0 1.3em 1.4em;
width: 40%;
min-width: 300px;
float: right;
}

.thumbinner {
  position: relative;
  width: 100%;
  padding-bottom: 56.25%;
}

.thumbinner p, .thumbcaption p {
margin: 0;
padding: 0;
}

.thumbinner p iframe {
  position: absolute;
  width: 100%;
  height: 100%;
}

.thumbcaption {
border: none;
line-height: 1.4em;
padding: 3px !important;
font-size: 94%;
text-align: left;
}

@media (max-width: 479px) {
table.infobox, .embedvideo {
width: 100%;
}
}

.spoiler {
margin-left:40px;
font-weight: bold;
}
[[/module]]
