[[module css]]
@supports(display:grid) {
  :root {

/* 色を変える部分です */
/* 下の"::root"の部分と統合しても大丈夫です */

    --gray-monochrome: 157,144,206; /* ホバー時のタブの背景色など*/
    --bright-accent: 109,138,239; /* owindowの背景色など*/
    --white-monochrome: 236, 247, 249; /* 全体の背景色など*/
    --pale-gray-monochrome: 218, 240, 243; /* トップバーの背景色など*/
    --light-gray-monochrome: 91,194,213; /* タブのコンテンツのボーダー色など*/
    --dark-gray-monochrome: 85,149,162; /* タブのセレクタのボーダー色など*/
    --black-monochrome: 39,37,32; /* 全体の文字色など*/
    --medium-accent: 67,69,188; /* ホバー時のリンクカラーなど*/
    --dark-accent: 24,21,129; /* リンクカラーなど*/

    /* SCPロゴ画像 */
    /* 高さは196pxが望ましい */
    --logo-image: url("http://scp-jp-storage.wikidot.com/local--files/file:6634493-2-tk8t/crocker_logo.png");

    /* サイトタイトル */
    --header-title: "氷我記";

    /* サブタイトル */
    --header-subtitle: "your ice age";
  }

  #extra-div-1 {
    /* ヘッダー背景画像 */
    /* 高さは196pxが望ましい */
    background-image: url("http://scp-jp-storage.wikidot.com/local--files/file:6634493-1-n8cl/crocker-header-bg.jpg");
  }
}

@media only screen and (max-width: 768px){
  #header h1 a::before {
    font-size: 3.5rem;
  }
  #header {
    background-size: auto 6.375rem;
  }
}


