[[include :scp-wiki-cn:theme:foxtrot dark=a]]

[[module css]]
:root {
    --accent: 187, 136, 255;
    --header-title: "RCT-Δt";
    --header-subtitle: "保卫多元宇宙罗盘";
    --darkmode-logo-img: url(https://scp-wiki.wdfiles.com/local--files/welcome-to-delta-t/Delta-t-transparent.png);
    --logo-opacity: 20% !important;
}

.log { 
    border: solid 2px rgba(0, 0, 0, 0.15);
    padding-left: 16px;
    padding-right: 16px;
    margin-bottom: 16px;
    margin-top: 16px;
    background: linear-gradient(to bottom, rgba(var(--accent), 0.25), #20242c 70px, #20242c 100px, #20242c 100%);
}

div.onto_memo {
    --memo-heading: "来自本质促动部的紧急通知";
    --memo-logo: url(https://scp-wiki.wdfiles.com/local--files/scp-6172/ontologo4.png);
    --memo-color: 238, 237, 250;
    color: #3b3b3b;
}

div[class$="_memo"] {
    background: var(--memo-logo) rgba(var(--memo-color), 1);
    background-size: contain;
    background-position: top center;
    background-repeat: no-repeat;
    min-height: 4rem;
    margin-inline: auto;
    margin-block: 2rem;
    padding-inline: 20px;
    padding-block: 10px;
    border: solid 2px rgba(0, 0, 0, 0.1);
    text-align: justify;
}

div[class$="_memo"]::before {
    content: var(--memo-heading);
    font-family: var(--UI-font);
    font-weight: 900;
    font-size: 1.3em;
    text-align: center;
    margin: 0 auto 1rem auto;
    display: flex;
    flex-direction: column;
    padding-top: 0.5em;
    color: #3b3b3b;
}

tt {
   font-size: 105%;
}

.code-delta {
   border-left: solid 5px rgb(var(--accent));
   padding: 0.325rem 1.25rem;
   margin: 0rem 1.25rem;
}

[[/module]]

