[[module css]]
@import url("https://nu-scptheme.github.io/Black-Highlighter/stable/styles/normalize.min.css");
@import url("https://nu-scptheme.github.io/Black-Highlighter/stable/styles/black-highlighter.min.css");

h1 {
    display: flex;
    justify-content: center;
}

div.pridebox,
div.includebox {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
}

div.pridebox div.insidebox {
    flex-basis: calc((100% / 2) - 0.125rem);
    border: 0.0625rem solid rgb(var(--bright-accent));
    flex-grow: 2;
    min-width: 18.750em;
}

div.insidebox div.imagebox {
    display: flex;
    justify-content: center;
}

div.insidebox div.imagebox > div {
    width: 50%;
    min-width: inherit;
}

div.includebox {
    text-align: center;
}

div.includebox > blockquote {
    flex-basis: calc(50% - 1em);
    flex-grow: 2;
    box-sizing: border-box;
    margin: 0.25em;
    width: calc(50% - 1em);
}

div.includebox tt {
    font-size: 80%;
}

div.pridebox div.insidebox h1, div.pridebox div.insidebox h2 {
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 900;
    padding: 0 1 1 1em;
}

#page-content > div.pridebox div.scp-image-block {
    box-shadow: inherit;
    border: initial;
    margin-bottom: 1em;
}

#page-content > div.pridebox div.scp-image-block > a {
    width: 90%;
}

#page-content > div.pridebox > div.scp-image-caption {
    border-top: inherit;
}

#page-content > div.scp-image-block {
    border-top: inherit;
}

.flipped {
    display: inline-block;
    transform: scaleX(-1);
}
[[/module]]

