function tomorrow( )
{
  var currentTime = new Date();
  var currentUTC = currentTime.getTime();
  var extraDay = 1000 * 60 * 60 * 24;
  var tomorrowTime = new Date(currentUTC + extraDay);

  // String padding
  var tomMonth = ( tomorrowTime.getMonth() + 1 < 10 ? "0" : "" ) + (tomorrowTime.getMonth() + 1);
  var tomDay = ( tomorrowTime.getDate() < 10 ? "0" : "" ) + tomorrowTime.getDate();
  document.getElementById("lolTroy").firstChild.nodeValue = "The date on the communique was listed as: " + tomorrowTime.getFullYear() + "/" + tomMonth + "/" + tomDay;
}
</script>
<p id="lolTroy">This text was recovered by having [[[SCP-187]]] examine the terminal at Site-██. Her copied text was then sent through [[[SCP-758]]] to correct it, and then translated by Dr. ████████. The date on the communique was listed as [REDACTED].</p>
<script type="text/javascript">
tomorrow();
</script>
