function tomorrow( )
{
  var currentTime = new Date();
  var currentUTC = currentTime.getTime();
  var extraDay = 1000 * 60 * 60 * 24;
  var tomorrowTime = new Date(currentUTC + extraDay);

  // String padding
  var tomMonth = ( tomorrowTime.getMonth() + 1 < 10 ? "0" : "" ) + (tomorrowTime.getMonth() + 1);
  var tomDay = ( tomorrowTime.getDate() < 10 ? "0" : "" ) + tomorrowTime.getDate();
  document.getElementById("lolTroy").firstChild.nodeValue = "성명서에 나와있던 날짜는 다음과 같다. " + tomorrowTime.getFullYear() + "/" + tomMonth + "/" + tomDay;
}
</script>
<p id="lolTroy">제██기지에서 [[[SCP-187]]]이 터미널을 검사하여 글을 복구하였다. 그녀가 복사한 글은 [[[SCP-758]]]에 보내어 바로잡았고, ████████ 박사가 번역하였다. 성명서에 쓰인 날짜는 [편집됨].</p>
<script type="text/javascript">
tomorrow();
</script>
