[[module CSS]]
@media (min-width: 768px) {
    #main-content { max-width: min(900px, calc(100% - 4rem)); }
}
:root {
  --header-subtitle: "安保设施";
  --sp_header-subtitle-scale: 2.125;
}
#header h1 a::before, #header h2 span::before {
    font-family: 'BauhausLTDemi', 'Nanum Gothic', 'Arial', sans-serif;
}

body {
    background-image: linear-gradient(
        to bottom,
        #FCFCFC, #FCFCFC 100%);
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 28px;
    position: absolute;
    background: url("https://scp-wiki.wdfiles.com/local--files/sigma-9-themes/SCP_F.png");
    background-size: contain;
    background-repeat: no-repeat;
    background-position: calc(50vw - 380px) 50%;
    z-index: -1;
}
@media (max-width: 767px) {
    div#extra-div-1 {
         opacity: 0.2;
         background-position: 50% 50%;
    }
}

.socontent.header h1 {
    font-size: 1.85rem;
    max-width: max-content;
    margin: 2.5rem auto 1rem;
}
.socontent.header h1 span {
    padding: 0 .25em;
    text-transform: uppercase;
}

.art-container {
    display: flex;
    flex-wrap: wrap;
    align-content: center;
    justify-content: space-around;
    align-self: flex-end
    gap: 1rem 1rem;
}
.art-container .art {
    flex: 0 1 250px;
    text-align: center;
    padding-bottom: 1rem;
}

.art-container .art .image-container {
    padding: 0;
    min-height: 100%;
    max-width: 100%;
}
[[/module]]

