/* admoseek */

div.blockquote.aigc {
  padding: 0;
}

.aigc .admohead {
  background-color: #ffffff20;
  font-family: 'Poppins', 'Noto Sans SC', sans-serif;
  text-align: center;
  padding-block: 0.5em;
}

.admohead span.icon {
  background: linear-gradient(to right, #fffef9, #fffef988 100%);
