}

.aigc p:first-of-type {
  margin-top: 0;
}

.aigc p:last-of-type {
  margin-bottom: 0;
}

#page-content div.ask {
  width: fit-content;
  max-width: calc(100% - 4rem);
  margin-right: 0;
  margin-left: auto;
  margin-block: 2rem;
  border-radius: 1rem;
  background-color: #41ae3c40;
  font-size: 110%;
  padding: 0.5em 1em;
}

.admoseek {
  position: relative;
  margin: 3rem 2rem 3rem 4rem;
  font-size: 110%;
