[[include :scp-wiki:theme:simple-yonder xmode=a]]

[[module CSS]]
div#header {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/coda-for-the-capitol/PENTAGRAM.png);
    background-size: 5.5rem;
    background-position: 9px 42.5px;
}

div#header h1 a span {
    font-size: 0px;
}

div#header h1 a:before {
    content: "PENTAGRAM";
    font-family: 'jostregular', sans-serif;
    font-weight: bold;
    color: #f4f4f4;
    text-shadow: none;
}

div#header h2 span {
    font-size: 0px;
    padding: 4px;
}

div#header h2:after {
    content: "US Paranatural Warfare Command";
    font-family: 'Open Sans', sans-serif;
    font-weight: bold;
    color: #f4f4f4;
    padding: 17px 0;
    text-shadow: 0px 0px 0px #000;
    white-space: pre;
}

#breadcrumbs {
    text-align: center;
}

.material-box {
    margin: 2%;
    font-family: "Fira Code", monospace;
}

@media (max-width: 767px) {
    div#header h2:after {
        font-size: 0.7em;
    }

    div#header {
        background-image: url(https://scp-wiki.wdfiles.com/local--files/coda-for-the-capitol/PENTAGRAM.png);
        background-size: 3.75rem;
        background-position: 5px 62.5px;
    }

    #header h1 a {
        padding: 85px 0 20px;
    }
}

@import url('https://fonts.googleapis.com/css2?family=Anton&family=Playfair+Display+SC&display=swap');

.newspaper {
    width: 95%;
    padding: 1px 15px;
    margin: auto;
    margin-top: 10px;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .2);
    font-family: serif;
    font-size: 1.2em;
}

.title {
    font-size: 3.75em;
    text-align: center;
    margin: 0.25em;
}

.subtitle {
    font-size: 1.75em;
    text-align: center;
    margin: -2.5px 0 7.5px;
}

.banner {
    font-family: serif;
    font-size: 120%;
    border-top-style: solid;
    border-bottom-style: solid;
    text-align: center;
    padding: 5px 0px;
}

.headline {
    font-family: 'Anton', serif;
    margin: 5px 0 7.5px;
    text-align: center;
}

.reporter {
    font-size: 110%;
    font-style: italic;
    text-align: center;
}

.article {
    text-align: justify;
    line-height: 1.4em;
    padding: 0em 0.5em;
}

.footnotes-footer {
    display: none;
}
[[/module]]

