    --sp_side-logo-scale: 0.75;
    --sp_header-height: 12.5rem;
    --sp_top-bar-height: 1.6rem;
    --sp_top-bar-gradient-top-color: #000;
    --sp_top-bar-gradient-bottom-color: #000;
    --sp_top-bar-link-color: #FFF;
    --sp_top-bar-link-hover-color: #000;
    --sp_top-bar-link-hover-background: #AAA;
    --sp_area-max-width: 50rem;
    --sp_rate-module-text-color: #000;
    --sp_rate-module-background: #DDD;
    --sp_rate-module-sub-color: #888;
    --sp_rate-module-active-color: #000;
    --sp_rate-module-button-color: #888;
    --sp_rate-module-button-background: #DDD;
    --sp_rate-module-button-hover-color: #DDD;
    --sp_rate-module-button-hover-background: #888;
}

body {
    background-color: #EEE;
    font-family: "Noto Sans SC", sans-serif;
    font-size: 0.875rem;
}

@media (min-width: 768px) {
    div.mobile-top-bar {
        display: block;
        height: 0 !important;
    }

    #side-bar {
        display: block;
        position: fixed;
        top: 0;
        left: calc(var(--sp_side-bar-width) * -1 - 3rem);
        width: var(--sp_side-bar-width);
        height: 100%;
        background-color: #FFF;
        overflow-y: auto;
        z-index: 100;
        padding: 1em 1em 0 1em;
        -webkit-transition: left 0.5s ease-in-out 0.1s;
        -moz-transition: left 0.5s ease-in-out 0.1s;
        -ms-transition: left 0.5s ease-in-out 0.1s;
        -o-transition: left 0.5s ease-in-out 0.1s;
        transition: left 0.5s ease-in-out 0.1s;
    }

    div.mobile-top-bar div.open-menu {
        display: block;
    }

    #top-bar .open-menu a {
        position: fixed;
        top: 0.5em;
        left: 0.5em;
        z-index: 5;
        font-family: 'Nanum Gothic', san-serif;
        font-size: 30px;
        font-weight: 700;
        width: 30px;
        height: 30px;
        line-height: 0.9em;
        text-align: center;
        border: 0.2em solid #888;
        background-color: #fff;
        border-radius: 3em;
        color: #888;
    }

    #top-bar .open-menu a:hover {
        text-decoration: none;
        -webkit-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -moz-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -ms-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        -o-box-shadow: 0 0 20px 3px rgba(153,153,153,1);
        box-shadow: 0 0 20px 3px rgba(153,153,153,1);
    }

    #side-bar:target {
        display: block;
        left: 0;
        width: var(--sp_side-bar-width);
        margin: 0;
        z-index: 10;
    }

    #side-bar:target + #main-content {
        left: 0;
    }

    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        background: rgba(0,0,0,0.3) 1px 1px repeat;
        z-index: -1;
    }

    div.mobile-top-bar ul {
        display: none !important;
    }
}

div#content-wrap {
    justify-contents: center;
}

div#header {
    background-image: linear-gradient(to bottom, #AAA, #AAA);
}

div#header::before {
    grid-row: 1/6;
    grid-column: 1/6;
    width: auto;
}

div#header::after {
    content: "";
    position: absolute;
    left: 0;
    bottom: var(--sp_top-bar-height);
    width: calc(var(--sp_header-height) * var(--sp_side-logo-scale));
    height: calc(var(--sp_header-height) * var(--sp_side-logo-scale));
    max-width: 25vw;
    max-height: 25vw;
    background-image: var(--sp_header-side-logo);
    background-position: center;
    background-repeat: no-repeat;
    background-size: 100% auto;
}

div#header h1 {
    grid-row: 3/4;
    grid-column: 2/4;
    display: block;
    max-width: unset;
    text-align: center;
    margin: 0;
    padding: 0;
}

div#header h2 {
    grid-row: 4/5;
    grid-column: 2/4;
    display: block;
    max-width: unset;
    text-align: center;
    margin: 0;
    padding: 0;
}

div#header h1 a,
div#header h2 span {
    display: inline-grid;
    font-weight: 900;
    justify-content: center;
    align-items: center;
}

div#header h1 a::before,
div#header h1 a::after {
    grid-column: 1 / 2;
    grid-row: 1 / 2;
    content: var(--header-title);
    font-size: var(--sp_header-title-size);
    font-family: var(--header-font);
    z-index: 5;
    text-shadow: none;
    padding: 0.5rem;
    letter-spacing: 0.25rem;
}

div#header h1 a::before {
    color: #000;
}

div#header h1 a::after {
    color: transparent;
    text-stroke: 0.4rem #AAA;
    -webkit-text-stroke: 0.4rem #AAA;
    z-index: 1;
}

div#header h2 span::before,
div#header h2 span::after {
    grid-column: 1 / 2;
    grid-row: 1 / 2;
    color: #000;
    content: var(--header-subtitle);
    font-size: var(--sp_header-subtitle-size);
    font-family: var(--header-font);
    letter-spacing: 0.05vw;
    z-index: 5;
    text-shadow: none;
    padding: 0 0.5rem;
}

div#header h2 span::after {
    color: transparent;
    text-stroke: 0.3rem #AAA;
    -webkit-text-stroke: 0.3rem #AAA;
    z-index: 1;
}

div#page-title,
input#search-top-box-input {
    display: none;
}

div#top-bar {
    box-shadow: 0 0.4rem 0 rgba(0, 0, 0, 0.6), 0 0.8rem 0 rgba(0, 0, 0, 0.4);
}

div#top-bar div[class*="top-bar"] > ul {
    justify-content: center;
}

div#login-status {
    color: #FFF;
    text-shadow: -1px 0 #000, 0 1px #000, 1px 0 #000, 0 -1px #000;
    z-index: 30;
}

div#account-options {
    text-shadow: none;
    text-align: center;
    background-color: #DDD;
    border: solid 3px #888;
    z-index: 35;
}

div#account-options a {
    color: #000;
}

div#account-options a:hover {
    text-decoration: none;
    background-color: #EEE;
}

form#search-top-box-form input[type="submit"] {
    color: #000;
    background-color: #EEE;
    background-image: none;
    border: solid 3px #888;
    border-radius: 0;
    box-shadow: none;
}

form#search-top-box-form input[type="submit"]:hover,
form#search-top-box-form input[type="submit"]:focus {
    color: #FFF;
    background-color: #888;
    background-image: none;
    border: solid 3px #888;
    border-radius: 0;
    box-shadow: none;
}

div#top-bar div.open-menu a {
    color: #000 !important;
    border: solid 3px #888;
    background-color: #EEE;
}

div#side-bar,
div#side-bar:target {
    border: none;
    background-color: #FFF;
    z-index: 36;
}

div#side-bar div.side-block {
    box-shadow: none;
    background-color: #DDD !important;
    border: solid 3px #888;
    border-radius: 0;
}

div#side-bar div.side-block img {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
}

div.page-rate-widget-box {
    margin-right: 0;
    padding: 0;
    box-shadow: none;
    border: solid 3px #888;
    border-radius: 0;
}

div.blockquote {
    font-size: 1rem;
    border: solid 3px #888;
    background-color: #DDD;
    margin: 2rem 0;
}

div.document {
    margin: 3rem;
}

div.image-photo {
    padding: 0.75rem;
    background-color: #FFF;
    width: 60%;
    margin: 4rem auto 0 auto;
    transform: rotate(4deg);
    -webkit-transform: rotate(4deg);
}

/* for HUBPAGE */
/* inspired by Sekai_s */

div.content-container-container {
    overflow-x: hidden;
}

div.content-container {
    display: grid;
    align-items: start;
    position: relative;
    grid-template-columns: 1fr 0.05fr 1fr 0.05fr 1fr 0.05fr 1fr;
    grid-template-areas: "p1 . p2 . p3 . p4";
    width: 415%;
    left: 0%;
    max-width: unset !important;
    transition: ease 0.5s;
}

div.content-container div.page1 {
    grid-area: p1;
}

div.content-container div.page2 {
    grid-area: p2;
}

