[[module CSS]]
.workdiv {
    --image-url: url("http://scpsandboxcn.wikidot.com/local--files/b-b-lm-a-sheep/%E7%9F%B3%E5%8C%96%E5%9F%8E");
    --titlecolor: #ffbe3d;
    color: var(--titlecolor);
    cursor: pointer;
    padding: 2rem;
    padding-left: 0rem;
    position: relative;
    transition: color 1s;
    z-index: 100;
    margin-right: 0.5em;
}

.workdiv:hover {
    --titlecolor: #fff;
    color: var(--titlecolor);
}

.workdiv::before {
    background: var(--image-url);
    position: absolute;
    width: 100%;
    height: 100%;
    content: "";
    background-position: left;
    transition: background 1s, rotate 1s, opacity 1.5s, border-left-color 1s, color 1s;
    z-index: -1;    
    border-top-right-radius: 1em;
    border-bottom-right-radius: 1em;
    opacity: 0.3;
    filter: grayscale(0.25);
    border-left: 0.5em #ffbe3d solid;
    background-size: 80rem;
}

.workdiv:hover::before {
    background-position: center;
    opacity: 1;
    border-left: 0.5em #888 solid;
}

.workdiv2 {
    margin: 1rem;
    color:var(--titlecolor);
    border-bottom: var(--titlecolor) 0.1em solid;
    transition: color 1s, border-bottom-color 1s !important;
}

.workdiv2 a{
    color:var(--titlecolor) !important;
    transition: color 1s;
}

.workdiv3 {
    margin: 1rem;
    color: var(--titlecolor);
    transition: color 1s !important;
}

.workdiv4 {
    margin-bottom: -4rem;
    text-align: right;
    display: flex;
    justify-content: flex-end;
    flex-wrap: nowrap;
    flex-direction: column-reverse;
    transition: color 1s !important;
}

.workdiv4 a{
    color:var(--titlecolor) !important;
    transition: color 1s;
}
[[/module]]

