[[module css]]

.page-tags {
    display: none;
}

#breadcrumbs {
    display: none;
}

body, #container-wrap-wrap, #container-wrap, #content-wrap, #page-content {
    min-height: unset !important;
}

.page-content {
    height: 95vh;
    position: sticky;
    top: 0px;
}

#header {
    background-position: 1em 4em;
    background-size: 88px 88px;
    display: none;
}

#content-wrap {
    margin: 0 auto 0;
    max-width: 1040px;
    padding: 0em 0em 0em 0em;
}

#main-content {
    max-width: 100%;
    margin: 0;
}

#container-wrap {
    background: none;
}

#page-content {
    min-height: 720px;
}

#container {
    background: white;
}

#page-title {
    display:none;
}

/*
div#page-options-container {
    display:none !important;
}
*/

/*页脚*/
#footer {
    display:none !important;
}

/*CC注明*/
.license-area {
    display:none;
}

#side-bar {
    display: none;
}

div#page-options-container {
    display:none !important;
}

table {
    border-spacing: 0px;
}

p {
    display: inline-block;
    margin-block-start: 0em;
    margin-block-end: 0px;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
}

.dialogue {
    transform: rotate(90deg);
    position: Fixed;
    right: 38vw;
    top: 98vw;
    width: 100vw;
    white-space: nowrap;
    color: #fff;
}

.character {
    transform: rotate(90deg);
    position: Fixed;
    right: 65vw;
    top: 23vw;
    width: 42vw;
    white-space: nowrap;
    text-align: right;
    font-size: 120%;
    color: #c0c0c0;
}

.background {
    height: 100vh;
    width: 100vw;
    z-index: -1;
    position: fixed;
    right: 0%;
    TOP: 0px;
}

.skip {
    transform: rotate(90deg);
    position: Fixed;
    right: -14vw;
    top: 70vh;
    width: 42vw;
    white-space: nowrap;
    text-align: right;
    font-size: 120%;
    color: #fff;
}

a {
    color: #fff;
}

a:visited {
    color: #fff;
}

.log {
    transform: rotate(90deg);
    position: Fixed;
    right: -1vw;
    top: 42vh;
    width: 100vw;
}

.map {
    height: 100vh;
    width: 100vw;
    z-index: -1;
    position: fixed;
    right: 0%;
    TOP: 0px;
}

.level {
    transform: rotate(90deg);
    position: Fixed;
    right: 31vw;
    top: 45vh;
    width: 38vw;
    filter: invert(0%);
    text-align: center;
    color: #fff;
}

.final {
    transform: rotate(90deg);
    position: Fixed;
    right: 0vw;
    top: 39.5vh;
    width: 100vw;
    text-align: center;
    color: #fff;
}

.tips {
    transform: rotate(90deg);
    position: Fixed;
    right: 41vw;
    top: 49.5vh;
    width: 100vw;
    filter: invert(0%);
    text-align: center;
    color: #fff;
    font-size: 90%;
}

.over {
    transform: rotate(90deg);
    position: Fixed;
    right: 52.5vw;
    top: 11.5vh;
    width: 50vw;
    color: #fff;
    padding-right: 2vh;
    border-right: solid #fff 1px;
}

.gain {
    transform: rotate(90deg);
    position: Fixed;
    right: 18vw;
    top: 59vh;
    width: 100vw;
    color: #fff;
}

.role {
    transform: rotate(90deg);
    position: Fixed;
    right: 20vw;
    top: -15vh;
    width: 100vw;
    z-index: -1;
}

.voice {
    transform: rotate(90deg);
    position: Fixed;
    right: -25vw;
    top: 56vh;
    width: 100vw;
    color: #fff;
}

.levelmap {
    height: 100vh;
    width: 100vw;
    z-index: -1;
    position: fixed;
    right: 0%;
    TOP: 0px;
}

.finalpic {
    height: 100vh;
    width: 92vw;
    z-index: -1;
    position: fixed;
    right: -28%;
    TOP: 0px;
}

@media screen and (orientation: portrait) {
  .landscape {
      display: none;
  } 
} 
@media screen and (orientation: landscape) {
  .portrait {
      display: none;
  } 
}

