[[code type="css"]]
/*
    Powered on 2020/03/09 23:44:50
    [2020 Wikidot Theme]
    Created by tutuji
    CC BY-SA 3.0
*/
@import url('https://fonts.googleapis.com/css?family=Noto+Serif+JP&display=swap');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnDotumBold.css');
@import url('https://scpko.wdfiles.com/local--files/unfont/UnDotum.css');

:root {
    --bg: #ffffff;
    --fg: #e30000;
}
body {
    background: rgb(255, 255, 255);
    color: rgb(0, 0, 0);
    font-family: 'Noto Serif JP', 'Dotum', 'UnDotum', serif;
}
a,a:visited {
    color: rgb(255, 10, 10);
}
h1 {
    color: rgb(220, 0, 0);
    font-family:  'Noto Serif JP', 'UnDotumBold', serif;
}
h2,h3,h4,h5,h6 {
    color: rgb(0, 0, 0);
    font-family:  'Noto Serif JP', 'UnDotumBold', serif;
}
blockquote {
    border: 1px dashed #ff0a0a;
    background: #ffffff;
}
div#page-title {
    color: #000000;
}
div#container-wrap {
    background: url(https://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A2206092-12-83ca/kirimi) top left repeat-x;
}
div#header {
    background: url(https://scp-jp-sandbox3.wdfiles.com/local--files/draft%3A2206092-12-83ca/%E5%8B%9D) 10px 40px no-repeat;
    background-position: 0em 0.5em;
    background-size: 130px 130px;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "회전초밥 승리";
    color: rgb(0, 0, 0);
    text-shadow: -1px -0.5px 4px #ffffff;
    font-family:  'Noto Serif JP', 'UnDotumBold', serif;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "스시블레이드 Sushiblade";
    font-weight: bold;
    color: rgb(50, 50, 50);
    padding: 19px 0;
    text-shadow: -1px -1px 3px rgba(255, 255, 255, 10);
    white-space: pre;
}
div#side-bar div.side-block {
    background: rgb(255, 255, 255);
    border: 1px solid #000000;
    box-shadow: 0 2px 6px rgba(255, 10, 10, 5);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(0, 0, 0);
    border-color: rgb(255, 255, 255);
}
@media (max-width: 767px) {
    #side-bar{
        background-color: var(--bg);
    }
}
div.scpnet-interwiki-wrapper {
    filter: hue-rotate(355deg) contrast(180%);
}
div.yui-navset div.yui-content {
    background: rgb(250, 250, 250);
}
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav a:focus {
    background: rgb(204, 204, 204);
}
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: rgb(200, 200, 200);
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgb(204, 0, 0)!important;
    border: solid rgb(204, 0, 0) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgb(204, 0, 0)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: rgb(255, 255, 255);
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: rgb(204, 0, 0)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}
div.open-menu a {
    color: rgb(204, 204, 204)
!important;
    border-color: rgb(0, 0, 0)
!important;
}
#page-content .scp-image-block {
    border-color: var(--fg);
    box-shadow: none;
}
#page-content .scp-image-block .scp-image-caption {
    border-color: var(--fg);
    background-color: var(--bg);
}
table.wiki-content-table th {
    background-color: var(--bg);
}
hr {
    background-color: var(--fg);
}
.code {
    border: 1px dashed var(--fg);
    background-color: transparent;
}
.hovertip {
    width: 20em;
    padding: 0.5em;
    border: 1px solid var(--fg) !important;
    border-radius: 0.5em;
    background-color: #ffffff !important;
    box-shadow: 0 0 6px 1px #c90000;
    z-index: 100;
}
.hovertip .f-heading {
    color: var(--fg);
}
[[/code]]
